UPDATE `locales_npc_text` SET `ID` = 7916,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Für mich war der Grund des Ozeans immer ein sehr friedlicher Ort.' WHERE `locales_npc_text`.`ID` = 7916;
UPDATE `locales_npc_text` SET `ID` = 7917,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist wunderbar, Euch wiederzusehen, $N! Ich kann Euch gar nicht genug dafür danken, dass ihr meinem lieben, kleinen Gnom diese Nachricht überbracht habt.$B$BWürdet Ihr gerne ein Lied hören? Es wird Euch die Reise durch die wogenden Wellen erleichtern.' WHERE `locales_npc_text`.`ID` = 7917;
