UPDATE `locales_npc_text` SET `ID` = 7933,`Text0_0_loc3` = 'Die Zeit ist gekommen, nun da das neue Jahr angebrochen ist und der Mond hell scheint.$B$BEs ist nun unsere Zeit... wenn die Alten erwachen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7933;
UPDATE `locales_npc_text` SET `ID` = 7935,`Text0_0_loc3` = 'In längst vergangenen Tagen lebten wir für die Jagd. Wir jagten nach Ruhm, nach Ehre...$B$BIst jetzt alles anders?',`Text0_1_loc3` = 'In längst vergangenen Tagen lebten wir für die Jagd. Wir jagten nach Ruhm, nach Ehre...$B$BIst jetzt alles anders?' WHERE `locales_npc_text`.`ID` = 7935;
UPDATE `locales_npc_text` SET `ID` = 7937,`Text0_0_loc3` = 'Eure Bemühungen Drachisch zu erlernen sind sinnlos, Sterblicher. Und weshalb strebt Ihr überhaubt danach, diese uralte Sprache zu erlernen? In der Tat verwunderlich...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7937;
UPDATE `locales_npc_text` SET `ID` = 7942,`Text0_0_loc3` = 'Lok''tar $R. Ich bin General Zog, Abgesandter der Horde. Ich werde Euch über die Anstrengungen der mächtigen Horde, was den Krieg von Ahn''Qiraj angeht, auf dem Laufenden halten. Wie kann ich Euch weiterhelfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7942;
