UPDATE `locales_npc_text` SET `ID` = 8333,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Bleibt unauffällig, ich warte darauf, dass der König mir eine Audienz gewährt. Das Warten macht mir aber nichts aus, ich fühle mich zwischen all diesen stämmigen Wachen recht sicher.' WHERE `locales_npc_text`.`ID` = 8333;
