UPDATE `locales_npc_text` SET `ID` = 8335,`Text0_0_loc3` = 'Seid gegrüßt, Freund! Könnte ich Euch eventuell an einigen ektoplasmischen Kondensatoren interessieren? Oder vielleicht an einem hübschen omnidirektionalen spektralen Assimilator?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8335;
