UPDATE `locales_npc_text` SET `ID` = 8527,`Text0_0_loc3` = 'Die ersten Schritte unseres Kampfes gegen die Geißel waren erfolgreich. Wir konnten einige Siege verbuchen und ich hoffe, dass meine Unterstützung uns weitere erfolgreiche Schlachten bringen wird.',`Text0_1_loc3` = 'Die ersten Schritte unseres Kampfes gegen die Geißel waren erfolgreich. Wir konnten einige Siege verbuchen und ich hoffe, dass meine Unterstützung uns weitere erfolgreiche Schlachten bringen wird.' WHERE `locales_npc_text`.`ID` = 8527;
