UPDATE `locales_npc_text` SET `ID` = 8534,`Text0_0_loc3` = 'Unsere Sankten entziehen den Strömungen von Leylinien, die unser Land durchziehen und mit bloßem Auge nicht sichtbar sind, arkane Macht.$B$BNach dem Verlust des Sonnenbrunnens stellen sie eine für uns stabile und zuverlässige Energiequelle dar.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8534;
UPDATE `locales_npc_text` SET `ID` = 8535,`Text0_0_loc3` = 'Es ist ein Sinnbild für die Unerschütterlichkeit unseres Volkes, dass Morgenluft noch steht. Wir werden der Geißel niemals wieder den Sieg überlassen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8535;
UPDATE `locales_npc_text` SET `ID` = 8536,`Text0_0_loc3` = 'Aus dem Wech, Landratte! Revilgaz hat mir den Galgen erspart, doch meine jetzige Strafe ist nicht viel besser.',`Text0_1_loc3` = 'Aus dem Wech, Landratte! Revilgaz hat mir den Galgen erspart, doch meine jetzige Strafe ist nicht viel besser.' WHERE `locales_npc_text`.`ID` = 8536;
UPDATE `locales_npc_text` SET `ID` = 8537,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr macht besser keinen Ärger, $N!' WHERE `locales_npc_text`.`ID` = 8537;
UPDATE `locales_npc_text` SET `ID` = 8538,`Text0_0_loc3` = 'Was kann ich für Euch tun, $N? Beeilt Euch, ich habe nicht den ganzen Tag Zeit!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8538;
UPDATE `locales_npc_text` SET `ID` = 8539,`Text0_0_loc3` = 'Was kann ich für Euch tun, $N? Nein, wartet! Was könntet Ihr für mich tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8539;
UPDATE `locales_npc_text` SET `ID` = 8541,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 8541;
