UPDATE `locales_npc_text` SET `ID` = 9163,`Text0_0_loc3` = 'Das alles ist für Euch, $N. Ihr habt das getan. Ihr habt all diese Leute und tausende mehr gerettet.$B$BIhr habt Euch über das Leid und die Tragödien dieser Welt erhoben und uns alle mit Euch gezogen.$B$BIhr habt so viel getan - mehr, als manch einer in zehn Leben erreichen könnte - und doch steht Ihr noch ganz am Beginn Eures Lebens. Wenn wir doch nur tausend Helden wie Euch hätten.$B$BAber hört gut zu, $N. Euer Schicksal liegt in der Scherbenwelt. Ich habe es in meinen Visionen gesehen, eine Prophezeiung, die noch nicht niedergeschrieben wurde.$B$BSucht unser Volk, $N. Sucht sie und bringt sie nach Hause...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9163;
UPDATE `locales_npc_text` SET `ID` = 9166,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9166;
UPDATE `locales_npc_text` SET `ID` = 9167,`Text0_0_loc3` = 'Ysiel hat bei ihrem Streifzug in die Scherbenwelt mit feindlicher Gegenwehr gerechnet. Deshalb sind wir Aufseher mitgekommen.$B$BWir haben nicht erwartet, dass wir auf Gegner in so großer Zahl und Stärke treffen, wie die Naga es hier sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9167;
UPDATE `locales_npc_text` SET `ID` = 9168,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe keine Ahnung wer Ihr seid, aber Eure Anwesenheit ist mehr als willkommen. Archimonde und seine Armee der Geißel können jeden Moment angreifen, $C. Werdet Ihr Euch gemeinsam mit uns dem Bösen stellen?' WHERE `locales_npc_text`.`ID` = 9168;
