UPDATE `locales_npc_text` SET `ID` = 9170,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wir müssen so lange durchhalten, wie wir können! Kämpft weiter!' WHERE `locales_npc_text`.`ID` = 9170;
