UPDATE `locales_npc_text` SET `ID` = 9176,`Text0_0_loc3` = 'Die Natur bevorzugt jene, die bereit sind, ihre Geheimnisse zu erlernen, $N.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9176;
UPDATE `locales_npc_text` SET `ID` = 9177,`Text0_0_loc3` = 'Die Natur ist eine starke Macht, $R. Jene, die versuchten sie zu bändigen, haben dies nie dauerhaft geschafft.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9177;
UPDATE `locales_npc_text` SET `ID` = 9179,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt, $N.' WHERE `locales_npc_text`.`ID` = 9179;
UPDATE `locales_npc_text` SET `ID` = 9180,`Text0_0_loc3` = 'Die Geister sind rastlos, $R. Achtet auf Eure Umgebung, während Ihr in Nagrand seid, oder Ihre Wut wird Euch verzehren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9180;
UPDATE `locales_npc_text` SET `ID` = 9181,`Text0_0_loc3` = 'Der Irdene Ring hat es sich zur Aufgabe gemacht, die Stärke und Größe der Elemente zu bewahren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9181;
UPDATE `locales_npc_text` SET `ID` = 9182,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wie kann mein Volk kämpfen, wenn die Elemente so in Aufruhr sind? Verstehen sie nicht, dass die Welt ohne die Unterstützung der Elemente einfach nicht existieren kann?' WHERE `locales_npc_text`.`ID` = 9182;
UPDATE `locales_npc_text` SET `ID` = 9183,`Text0_0_loc3` = 'Ich bin Untrag vom Irdenen Ring. Versucht, die Elemente nicht zu stören, während Ihr Euch am Thron der Elemente aufhaltet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9183;
UPDATE `locales_npc_text` SET `ID` = 9184,`Text0_0_loc3` = 'Ich spüre Eure Gier nach Macht, $C!',`Text0_1_loc3` = 'Ich spüre Eure Gier nach Macht, $C!' WHERE `locales_npc_text`.`ID` = 9184;
