UPDATE `locales_npc_text` SET `ID` = 9186,`Text0_0_loc3` = 'Ich habe keine Zeit für Predigten, $C. Sucht Euer Wissen woanders.',`Text0_1_loc3` = 'Ich habe keine Zeit für Predigten, $C. Sucht Euer Wissen woanders.' WHERE `locales_npc_text`.`ID` = 9186;
UPDATE `locales_npc_text` SET `ID` = 9187,`Text0_0_loc3` = 'Mir scheint, Ihr habt die falsche Abzweigung genommen, $C.',`Text0_1_loc3` = 'Mir scheint, Ihr habt die falsche Abzweigung genommen, $C.' WHERE `locales_npc_text`.`ID` = 9187;
UPDATE `locales_npc_text` SET `ID` = 9188,`Text0_0_loc3` = 'Seid gegrüßt, $N. Ich hoffe doch, dass Euch niemand gefolgt ist.',`Text0_1_loc3` = 'Seid gegrüßt, $N. Ich hoffe doch, dass Euch niemand gefolgt ist.' WHERE `locales_npc_text`.`ID` = 9188;
UPDATE `locales_npc_text` SET `ID` = 9189,`Text0_0_loc3` = 'Wir haben nichts zu besprechen, $C. Vielleicht solltet Ihr nach Gleichgesinnten suchen.',`Text0_1_loc3` = 'Wir haben nichts zu besprechen, $C. Vielleicht solltet Ihr nach Gleichgesinnten suchen.' WHERE `locales_npc_text`.`ID` = 9189;
UPDATE `locales_npc_text` SET `ID` = 9190,`Text0_0_loc3` = 'Es tut gut, hier mal jemanden anzutreffen, der noch bei Verstand ist. Was kann ich für Euch tun?',`Text0_1_loc3` = 'Es tut gut, hier mal jemanden anzutreffen, der noch bei Verstand ist. Was kann ich für Euch tun?' WHERE `locales_npc_text`.`ID` = 9190;
UPDATE `locales_npc_text` SET `ID` = 9191,`Text0_0_loc3` = 'Euch mangelt es offensichtlich an Verstand, um die Feinheiten des Arkanen zu erfassen, $C. Hinfort mit Euch.',`Text0_1_loc3` = 'Euch mangelt es offensichtlich an Verstand, um die Feinheiten des Arkanen zu erfassen, $C. Hinfort mit Euch.' WHERE `locales_npc_text`.`ID` = 9191;
UPDATE `locales_npc_text` SET `ID` = 9192,`Text0_0_loc3` = 'Wie kann ich Euch bei Euren Studien behilflich sein?',`Text0_1_loc3` = 'Wie kann ich Euch bei Euren Studien behilflich sein?' WHERE `locales_npc_text`.`ID` = 9192;
UPDATE `locales_npc_text` SET `ID` = 9193,`Text0_0_loc3` = 'Ein eiserner Wille und unerschütterliche Entschlossenheit zeichnet die unseren aus. Ihr scheint keine dieser Eigenschaften Euer Eigen zu nennen, $C.',`Text0_1_loc3` = 'Ein eiserner Wille und unerschütterliche Entschlossenheit zeichnet die unseren aus. Ihr scheint keine dieser Eigenschaften Euer Eigen zu nennen, $C.' WHERE `locales_npc_text`.`ID` = 9193;
UPDATE `locales_npc_text` SET `ID` = 9194,`Text0_0_loc3` = 'Dieses Land wurde durch den Absturz verwüstet. Auch wenn Ihr unseren Lehren nicht folgen mögt, $C, so bitte ich Euch doch, was immer in Eurer Macht steht zu tun, um dem Heilungsprozess zu helfen.',`Text0_1_loc3` = 'Dieses Land wurde durch den Absturz verwüstet. Auch wenn Ihr unseren Lehren nicht folgen mögt, $C, so bitte ich Euch doch, was immer in Eurer Macht steht zu tun, um dem Heilungsprozess zu helfen.' WHERE `locales_npc_text`.`ID` = 9194;
UPDATE `locales_npc_text` SET `ID` = 9195,`Text0_0_loc3` = 'Seid gegrüßt, $G Bruder : Schwester;. Benötigt Ihr geistige Führung?',`Text0_1_loc3` = 'Seid gegrüßt, $G Bruder : Schwester;. Benötigt Ihr geistige Führung?' WHERE `locales_npc_text`.`ID` = 9195;
UPDATE `locales_npc_text` SET `ID` = 9196,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Mögen die Winde Euch leiten, $N.' WHERE `locales_npc_text`.`ID` = 9196;
UPDATE `locales_npc_text` SET `ID` = 9197,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9197;
UPDATE `locales_npc_text` SET `ID` = 9199,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe es schon immer gemocht, Dinge zu sortieren und zu organisieren. Ich glaube, das kommt von meiner Mutter.' WHERE `locales_npc_text`.`ID` = 9199;
UPDATE `locales_npc_text` SET `ID` = 9201,`Text0_0_loc3` = 'Ich weiß noch nicht, was ich von Eurer Art halten soll, $R. Einige von Euch sind gewalttätig und töten alle Sporlinge, die sie sehen.$B$BIhr seht freundlich aus... aber ein bisschen seltsam, denke ich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9201;
UPDATE `locales_npc_text` SET `ID` = 9202,`Text0_0_loc3` = 'Ich heiße Euch in Sporeggar willkommen, $R. Normalerweise bekommen wir hier nicht viele von Euer Art zu Gesicht, aber Ihr habt Euren Wert für uns bewiesen. Fühlt Euch wie zu Hause.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9202;
UPDATE `locales_npc_text` SET `ID` = 9203,`Text0_0_loc3` = 'Die Sumpflords verschlingen die Sporenbeutel! Sie müssen aufgehalten werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9203;
UPDATE `locales_npc_text` SET `ID` = 9204,`Text0_0_loc3` = 'Dank Euch sind unsere Jungen jetzt sicher, $N. Geht nach Sporeggar und sprecht mit meinem Volk. Sie haben große Dinge über Euch gehört.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9204;
UPDATE `locales_npc_text` SET `ID` = 9206,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9206;
UPDATE `locales_npc_text` SET `ID` = 9208,`Text0_0_loc3` = '<Die Erde poltert als Gordawgs Bewegungen zum Halten kommen.>$B$BSprecht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9208;
UPDATE `locales_npc_text` SET `ID` = 9218,`Text0_0_loc3` = 'Mein Vater hat sich in exotischere Gefilde im Süden auf die Jagd nach größerer Beute gemacht. Aber keine Sorge, wir jagen hier immer noch!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9218;
UPDATE `locales_npc_text` SET `ID` = 9219,`Text0_0_loc3` = 'Seid wachsam, $C. Ihr wisst nie, wann ein Wildtierchen Euch jagt anstatt anders herum.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9219;
UPDATE `locales_npc_text` SET `ID` = 9220,`Text0_0_loc3` = 'Hört zu, $GFreund:Freundin;. Ich habe die Expedition in die fähigen Hände meines Sohnes Junior übergeben. Wenn man die mächtigsten Tiere von Azeroth gejagt hat, sehnt man sich nach etwas anderem.$B$BUnd habt Ihr die Größe der Wildtiere hier gesehen?$B$BIch bin hier, um die gefährlichsten Tiere zu jagen, die Euer Auge je erblickt hat. Ich plane, Zahna einzusacken und ihr Herz als Trophäe zu nehmen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9220;
UPDATE `locales_npc_text` SET `ID` = 9221,`Text0_0_loc3` = '$N! Ich erinnere mich gut an Euer Können als $GJäger:Jägerin;. Euer Anfang im Schlingendorntal mag etwas schwierig gewesen sein, aber Ihr habt durchgehalten und seid in den Kreis der Großen eingetreten, als Ihr den alten Bangalash eingesackt habt.$B$BAber hört zu, die Beute hier in der Scherbenwelt ist die wahre Herausforderung. Ich habe Narben, die das beweisen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9221;
UPDATE `locales_npc_text` SET `ID` = 9226,`Text0_0_loc3` = 'Ich heiße Euch in unserem bescheidenen Tempel willkommen, Geist.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9226;
UPDATE `locales_npc_text` SET `ID` = 9227,`Text0_0_loc3` = 'Mit den Draenei verhandeln, die uns den Rücken gekehrt haben während wir litten? Ich glaube nicht.$B$BEin wahrer Geist spricht nicht so! Zeigt Euer wahres Gesicht! Wer hat Euch geschickt?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9227;
UPDATE `locales_npc_text` SET `ID` = 9229,`Text0_0_loc3` = 'Wir sollen also denjenigen vertrauen, die uns mit Täuschungen beleidigen? Halten die Draenei so wenig von uns, dass sie auf Betrügereien angewiesen sind, um unsere Aufmerksamkeit zu erlangen?$B$BWir sind uns ihres Wunsches bewusst, uns zu ''rehabilitieren'', aber darauf sind wir nicht angewiesen. Wir sind in der Lage, für uns selbst einzustehen, und werden unseren Weg als stolzes, eigenes Volk gehen. Bringt diese Nachricht Euren Verbündeten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9229;
UPDATE `locales_npc_text` SET `ID` = 9230,`Text0_0_loc3` = 'Die Brennende Legion setzt uns ganz schön unter Druck, aber unsere Verteidigung hält stand. Jede Sekunde, die wir Archimonde vom Weltenbaum fernhalten können, bringt uns dem Sieg näher.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9230;
UPDATE `locales_npc_text` SET `ID` = 9231,`Text0_0_loc3` = 'Gut. Stellt sicher, dass sie es verstehen.$B$BNun würde ich es vorziehen, wenn Ihr meinen Tempel in Frieden lasst.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9231;
UPDATE `locales_npc_text` SET `ID` = 9235,`Text0_0_loc3` = 'Willkommen in der Oreborzuflucht, $GFreund:Freundin;. Ihr habt hier nichts zu fürchten. Wir sind Zerschlagene, aber nicht Eure Feinde.$B$BIch bin Ikuti von den Kurenai, einer Gruppe Zerschlagener, die wieder Verbindung zu den Draenei und ihren neuen Verbündeten aufnehmen wollen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9235;
UPDATE `locales_npc_text` SET `ID` = 9236,`Text0_0_loc3` = 'Maktu erkennt Euch nicht, $R, aber ist erfreut, Eure Bekanntschaft zu machen. Falls Ihr jemals Rüstungen kaufen oder reparieren wollt, kommt einfach zu Maktu.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9236;
UPDATE `locales_npc_text` SET `ID` = 9237,`Text0_0_loc3` = 'Erinnerungen an diesen Tempel verschleiern meine Gedanken wie ein dichter Nebel, aber ich kann sie nicht zu einer sinnvollen Geschichte zusammenfügen. Dieser Ort bedeutete mir einmal etwas. Bis ich diese Erinnerung wiederherstellen kann, fühle ich mich, als ob ein Teil von mir fehlt.$B$BVielleicht können die Draenei mir helfen, wiederzuerlangen, was ich verloren habe.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9237;
UPDATE `locales_npc_text` SET `ID` = 9238,`Text0_0_loc3` = 'Es gibt Leute, die mich ''Tim'' nennen, aber für meine Freunde bin ich ''Der Spezialist''.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9238;
UPDATE `locales_npc_text` SET `ID` = 9239,`Text0_0_loc3` = 'Wenn Ihr das fragen müsst, kennt Ihr mich bei weitem nicht gut genug, um diese Anrede zu benutzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9239;
UPDATE `locales_npc_text` SET `ID` = 9240,`Text0_0_loc3` = 'Grüße, $C. Ihr wollt die Grenzen Eurer Jagdkunst erkunden? Dann seid Ihr hier richtig.$B$BHemet Nesingwary hat die großartigste Expedition zusammengestellt, die diese Welt je gesehen hat. Schließt Euch unserer Safari an, wenn Ihr den Mut dazu habt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9240;
UPDATE `locales_npc_text` SET `ID` = 9241,`Text0_0_loc3` = 'Lasst Euch von den Ruinen um uns herum nicht täuschen, $C. Dalaran ist so stark wie eh und je.',`Text0_1_loc3` = 'Lasst Euch von den Ruinen um uns herum nicht täuschen, $C. Dalaran ist so stark wie eh und je.' WHERE `locales_npc_text`.`ID` = 9241;
UPDATE `locales_npc_text` SET `ID` = 9242,`Text0_0_loc3` = 'Karazhan ist mehr als ein einfaches Gebäude, $N. Wenn Ihr die Ley-Linien betrachtet, die Azeroth durchziehen, treffen sich alle ziemlich genau hier.$B$B<Alturus zeigt auf einen unsichtbaren Punkt unter dem Turm.>$B$BFaszinierend, oder?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9242;
UPDATE `locales_npc_text` SET `ID` = 9243,`Text0_0_loc3` = 'Die Naaru sind weiser, als wir begreifen können. Ich habe in den letzten fünf Monaten mehr von A''dal gelernt, als in all den Jahren in den Bibliotheken in Dalaran.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9243;
UPDATE `locales_npc_text` SET `ID` = 9244,`Text0_0_loc3` = 'Hier werden Steckbriefe von bekannten Verbrechern und bösartigen Tieren aufgehängt. Es wäre viel zu gefährlich für Euch, Euch jetzt schon auf die Jagd nach den Kopfgeldern hier zu machen, aber schaut wieder vorbei, wenn Ihr etwas stärker seid.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9244;
UPDATE `locales_npc_text` SET `ID` = 9245,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9245;
UPDATE `locales_npc_text` SET `ID` = 9246,`Text0_0_loc3` = 'Dieses Schild zeigt verschiedene Ankündigungen und Kopfgeldausschreibungen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9246;
UPDATE `locales_npc_text` SET `ID` = 9248,`Text0_0_loc3` = 'Willkommen in Zabra''jin, $C. Ich bin Schattenjäger Denjai, Aufseher dieses Außenpostens und Befehlshaber der Streitkräfte der Dunkelspeere hier in den Zangarmarschen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9248;
UPDATE `locales_npc_text` SET `ID` = 9249,`Text0_0_loc3` = 'Denkt nicht, Ihr könntet mit meinen Murlocbabys abhauen! Ich habe die Augen eines Falken und Ohren wie ein... wie ein... wie irgendetwas mit mächtig guten Ohren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9249;
UPDATE `locales_npc_text` SET `ID` = 9250,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Tor''gash glaubt, er kann die Zukunft aus den Innereien von Vögeln lesen, oder durch das Werfen der Knochen seiner Vorfahren. Ehrlich, alles was man tun muss, ist die Geister zu fragen. Aber versucht nicht, ihm das zu erklären, $N, er ist sehr dickköpfig.' WHERE `locales_npc_text`.`ID` = 9250;
UPDATE `locales_npc_text` SET `ID` = 9256,`Text0_0_loc3` = 'Ihr habt bewiesen, dass Ihr $Gein sehr fähiger Großwildjäger:eine sehr fähige Großwildjägerin; seid, $N. Aber es gibt immer noch ein Wildtier, von dem Ihr nur träumen könnt... Zahna!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9256;
UPDATE `locales_npc_text` SET `ID` = 9257,`Text0_0_loc3` = 'Ihr habt sie alle eingesackt: Bach''lor, Banthar, Aaskralle, sogar die mächtige Zahna! Ihr seid wahrlich $Gder beste Großwildjäger, den:die beste Großwildjägerin, die; ich kenne!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9257;
UPDATE `locales_npc_text` SET `ID` = 9258,`Text0_0_loc3` = 'Da rang ich mit einem Talbukhirsch im Unterholz, Auge in Auge, nur mit meinem Messer bewaffnet... <hust> und meinem Können als Jäger, um mich zu retten.$B$BPlötzlich haben mich zwei Windrocjäger aus der Luft angegriffen! Ihr feiger Angriff... <hust> hat ihnen aber nichts gebracht.$B$BIch habe ihre Schnäbel als Trophäen, aber eine infizierte Kratzwunde abbekommen. Hässlich, und tut mächtig weh, aber ich werde es überleben und in ein paar Tagen geht es mir wieder besser.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9258;
UPDATE `locales_npc_text` SET `ID` = 9259,`Text0_0_loc3` = 'Als ich das erste mal Pilze gesehen haben, die höher als Häuser waren, war ich mir nicht mehr so sicher, diesen Ort meine Heimat nennen zu können. Nach einer Weile fällt das aber gar nicht mehr auf.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9259;
UPDATE `locales_npc_text` SET `ID` = 9260,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Vergesst nicht, einige Zeit mit Angeln zu verbringen, während Ihr in den Zangarmarschen seid. Ihr wisst nicht wann ihr wieder Wasser sehen werdet.' WHERE `locales_npc_text`.`ID` = 9260;
UPDATE `locales_npc_text` SET `ID` = 9261,`Text0_0_loc3` = 'Telredor war einst ein abgeschiedener Rückzugsort für Draeneipriester und Pilger. Die ansässigen Priester erhielten selten Besuch.$B$BAls die Orcs begannen, Draeneisiedlungen anzugreifen, kamen Flüchtlinge in größerer Zahl. Anachoret Ahuurn und die anderen machten aus Telredor einen sicheren Hafen. Viele Draenei, mich eingeschlossen, verdanken seinen Anstrengungen unser Leben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9261;
UPDATE `locales_npc_text` SET `ID` = 9262,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Aussicht von der Spitze Telredors aus ist großartig, oder?' WHERE `locales_npc_text`.`ID` = 9262;
UPDATE `locales_npc_text` SET `ID` = 9263,`Text0_0_loc3` = 'Willkommen in Telredor, $C. Falls Ihr irgendetwas benötigt, zögert nicht, zu fragen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9263;
UPDATE `locales_npc_text` SET `ID` = 9264,`Text0_0_loc3` = 'Ich habe noch nie so viele verschiedene Pilze an einem Ort gesehen. Wenn ich genug Zeit hätte, könnte ich sicherlich für jeden von ihnen eine Verwendung finden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9264;
UPDATE `locales_npc_text` SET `ID` = 9265,`Text0_0_loc3` = 'In den letzten 20 Jahren hat Telredor mehr Veränderungen gesehen als in den vergangenen Jahrhunderten. Ich kann mich nicht beschweren, $N, denn die Welt hat sich in dieser Zeit noch mehr verändert.$B$BDas Licht hat mir Freunde und Kameraden gegeben, die ich als Priester im Kloster nie hätte treffen können. Lernt so viel wie möglich über die Vergangenheit, während Ihr hier seid. Es wird Euch helfen, zu verstehen, wohin Eure Reise geht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9265;
UPDATE `locales_npc_text` SET `ID` = 9267,`Text0_0_loc3` = 'Schattenjäger Denjai hat mich mit einem kleinen Trupp Arbeiter und Krieger hergeschickt, um diesen Außenposten zu errichten. Wir sind gerade fertig mit aufbauen, und er verlangt schon Berichte!$B$BWenn Ihr Vorräte benötigt, kann ich Euch ein paar Sachen anbieten, aber wir warten immer noch auf unsere erste Lieferung.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9267;
UPDATE `locales_npc_text` SET `ID` = 9268,`Text0_0_loc3` = 'Falls Ihr jemals Hexendoktor Tor''gash trefft, sagt ihm, seine Tränke und Glücksbringer funktionieren nicht! Ich habe genau gemacht, was er sagte, und immer noch hat Magasha nur Augen für Zurai.$B$BGlaubt nichts, was dieser Scharlatan von einem Hexendoktor Euch erzählt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9268;
UPDATE `locales_npc_text` SET `ID` = 9269,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Reavij hat die Feinfühligkeit eines Kodos! Das Lustige ist, er weiß nicht mal, wie offensichtlich das ist. Ich sehe, wie er mich anstarrt, wenn er denkt, niemand schaut zu.$B$BER ist mir unheimlich. Ich wünschte wirklich, Schattenjäger Denjai hätte ihn nicht mit uns geschickt. Die Einrichtung des Sumpfrattenpostens wäre die Gelegenheit gewesen, ihn loszuwerden.' WHERE `locales_npc_text`.`ID` = 9269;
UPDATE `locales_npc_text` SET `ID` = 9270,`Text0_0_loc3` = 'Hallo, $C. Ich gehöre zu denen, die als die Zerschlagenen bezeichnet werden. Wie die Verirrten waren wir ursprünglich Draenei und wurden durch Teufelsenergien transformiert, doch wir sind nicht ganz so stark betroffen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9270;
UPDATE `locales_npc_text` SET `ID` = 9271,`Text0_0_loc3` = 'Garadar liegt in Trümmern, $GFremder:Fremde;. Seit die Großmutter krank wurde, ist Garrosh in tiefe Depressionen verfallen. Er ist offensichtlich nicht seines Vaters Sohn.$B$B<Kurkush seufzt.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9271;
UPDATE `locales_npc_text` SET `ID` = 9274,`Text0_0_loc3` = 'Hier sind Bekanntmachungen über Belohnungen für die Anführer der Naga der Dunkelkämme und Blutschuppen aufgeführt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9274;
UPDATE `locales_npc_text` SET `ID` = 9275,`Text0_0_loc3` = 'Sie starben. Alle von ihnen...$B$BAls die Finsterblut uns überrannten, ergriff etwas Besitz von mir, dass ich nicht verstehen kann. Sagt mir, habt Ihr jemals eine Wut gespürt, so groß, das Euer ganzes Wesen in Flammen stand? Eine Hitze, die alles Leben auslöscht. Eine Hitze, die das Blut in Euren Adern zu Asche werden lässt? Das ist es, was ich fühlte.$B$B<Saurfangs Augen leuchten einen Moment lang rot.>$B$BIch war der letzte Überlebende. 30 von ihnen hatten mich umzingelt... Finsterblut... danach kann ich mich an nichts mehr erinnern. Mir wurde schwarz vor Augen, und ich erwachte einige Zeit später.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9275;
UPDATE `locales_npc_text` SET `ID` = 9276,`Text0_0_loc3` = 'Finstere Zeiten stehen uns bevor, $GFremder:Fremde;...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9276;
UPDATE `locales_npc_text` SET `ID` = 9277,`Text0_0_loc3` = 'Die Geister sind sehr aufgeregt. Wir müssen den Grund für ihre Verstörung herausfinden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9277;
UPDATE `locales_npc_text` SET `ID` = 9283,`Text0_0_loc3` = 'Hemet ist der größte Jäger, den ich kenne. Wir trafen uns vor vielen Monden durch Zufall in den Hügeln von Winterquell. Er und ich waren auf dieselbe Beute aus: einen großen Frostsäbler namens Rak''shiri.$B$BAnstatt gegeneinander um die Beute zu kämpfen, machten wir eine Wette: derjenige, der das Fell des Frostsäblers erlangte, konnte von dem anderen etwas fordern. Zu meiner ewigen Schande habe ich damals meinen Spitznamen erhalten - Fitz.$B$BSehe ich wie ein Fitz aus?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9283;
UPDATE `locales_npc_text` SET `ID` = 9284,`Text0_0_loc3` = '$GDer große Jäger:Die große Jägerin;, $N, ist zurückgekehrt. Erzählt mir von Eurer neuesten Beute.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9284;
UPDATE `locales_npc_text` SET `ID` = 9286,`Text0_0_loc3` = 'Ja, ja, ich fühle mich besser, aber irgendwie mag ich es hier unten.',`Text0_1_loc3` = 'Ja, ja, ich fühle mich besser, aber irgendwie mag ich es hier unten.' WHERE `locales_npc_text`.`ID` = 9286;
UPDATE `locales_npc_text` SET `ID` = 9287,`Text0_0_loc3` = 'Das Konsortium plante diese Operation schon seit Monaten! Wir sollten nach Oshu''gun graben, dem größten Diamanten des Universums.$B$BMinuten bevor wir anfangen wollten, erhielten wir eine Botschaft von Nexusprinz Haramad, dass wir unsere Tätigkeiten einstellen sollen, bis einige politische Angelegenheiten im Nethersturm geregelt wären. In der Zwischenzeit habe ich unsere Organisation in eine Sammelzentrale für geschmuggelte Edelsteine umgewandelt, doch so langsam frisst die ganze Operation unsere Profite auf.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9287;
UPDATE `locales_npc_text` SET `ID` = 9288,`Text0_0_loc3` = 'Gezhe ist hier, um eine Aufgabe zu erledigen. Ich bin hier, um sicherzustellen, dass ihn niemand dabei stört.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9288;
UPDATE `locales_npc_text` SET `ID` = 9289,`Text0_0_loc3` = 'Wisst Ihr wer wir sind, $GFremder:Fremde;? Wir sind Mag''har... braune Orcs werden wir von Euresgleichen genannt. Die Mag''har sind Überlebende: Überlebende einer gefallenen Dynastie, Überlebende der roten Pocken, Überlebende einer zerschmetterten Welt...$B$BIch bin Jorin, Sohn von Kilrogg; Letzter aus dem Geschlecht Totauge; Kriegshäuptling des Klans des Blutenden Auges.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9289;
UPDATE `locales_npc_text` SET `ID` = 9290,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wir wären nicht abgestürzt, wenn Hemet lernen könnte, nicht durch das Querruder zu schießen, wenn er auf Beute am Boden aus ist.$B$BAber keine Sorge, ich werde das schnell reparieren.' WHERE `locales_npc_text`.`ID` = 9290;
UPDATE `locales_npc_text` SET `ID` = 9292,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9292;
UPDATE `locales_npc_text` SET `ID` = 9296,`Text0_0_loc3` = 'Seid gegrüßt, $N.',`Text0_1_loc3` = 'He! Habe ich Euch nicht schon mal ein Bündel mit Tierhäuten gegeben?$B$BEgal, Ich bin froh, dass Ihr mich aus den Klauen des bösen Diebus Faulfaust gerettet habt.' WHERE `locales_npc_text`.`ID` = 9296;
UPDATE `locales_npc_text` SET `ID` = 9297,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Großmutter hat alle von uns aufgezogen. Sie ist für alle Mag''hari, die Ihr seht, etwas ganz besonderes. Wir werden sie vermissen, wenn sie von uns geht.' WHERE `locales_npc_text`.`ID` = 9297;
UPDATE `locales_npc_text` SET `ID` = 9312,`Text0_0_loc3` = 'Ihr seht vernünftig aus, $R. Vielleicht können wir uns gegenseitig von Nutzen sein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9312;
UPDATE `locales_npc_text` SET `ID` = 9315,`Text0_0_loc3` = 'Seid stark. Kael''thas hat uns verraten und wir erhalten ständig neue Nachrichten - doch verliert nicht den Glauben. Wir werden durchhalten, so wie wir schon immer durchgehalten haben. Arthas hat nicht unsere Seele vernichtet, als er wie ein Sturm über unser Land hinwegfegte, und Kael''thas wird es genauso wenig gelingen. Lasst dies uns allen im Lichte unserer blühenden Zukunft eine Lehre sein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9315;
UPDATE `locales_npc_text` SET `ID` = 9316,`Text0_0_loc3` = 'Wohin in unserer schönen Stadt möchtet Ihr gehen?',`Text0_1_loc3` = 'Wohin in unserer schönen Stadt möchtet Ihr gehen?' WHERE `locales_npc_text`.`ID` = 9316;
UPDATE `locales_npc_text` SET `ID` = 9317,`Text0_0_loc3` = 'Es gibt zwei Auktionshäuser in Silbermond. Welches bevorzugt Ihr?',`Text0_1_loc3` = 'Es gibt zwei Auktionshäuser in Silbermond. Welches bevorzugt Ihr?' WHERE `locales_npc_text`.`ID` = 9317;
UPDATE `locales_npc_text` SET `ID` = 9318,`Text0_0_loc3` = 'Das westliche Auktionshaus befindet sich in der Mitte des Basars, im Westen der Straße der Urahnen.',`Text0_1_loc3` = 'Das westliche Auktionshaus befindet sich in der Mitte des Basars, im Westen der Straße der Urahnen.' WHERE `locales_npc_text`.`ID` = 9318;
UPDATE `locales_npc_text` SET `ID` = 9319,`Text0_0_loc3` = 'Das Auktionshaus des königlichen Markts befindet sich im Osten der Stadt, direkt südlich der Trainingsstätte für Blutritter und Waldläufer.',`Text0_1_loc3` = 'Das Auktionshaus des königlichen Markts befindet sich im Osten der Stadt, direkt südlich der Trainingsstätte für Blutritter und Waldläufer.' WHERE `locales_npc_text`.`ID` = 9319;
UPDATE `locales_npc_text` SET `ID` = 9320,`Text0_0_loc3` = 'Welchem Pfad möchtet Ihr folgen?',`Text0_1_loc3` = 'Welchem Pfad möchtet Ihr folgen?' WHERE `locales_npc_text`.`ID` = 9320;
UPDATE `locales_npc_text` SET `ID` = 9321,`Text0_0_loc3` = 'Die Bank von Silbermond kann im Basar gefunden werden. Sucht in der südöstlichen Ecke.',`Text0_1_loc3` = 'Die Bank von Silbermond kann im Basar gefunden werden. Sucht in der südöstlichen Ecke.' WHERE `locales_npc_text`.`ID` = 9321;
UPDATE `locales_npc_text` SET `ID` = 9322,`Text0_0_loc3` = 'Wenn Ihr eine Bank im östlichen Teil der Stadt sucht, braucht Ihr nur zum königlichen Markt zu gehen. Die Bank befindet sich in dessen Nordmauer, ',`Text0_1_loc3` = 'Wenn Ihr eine Bank im östlichen Teil der Stadt sucht, braucht Ihr nur zum königlichen Markt zu gehen. Die Bank befindet sich in dessen Nordmauer, ' WHERE `locales_npc_text`.`ID` = 9322;
UPDATE `locales_npc_text` SET `ID` = 9323,`Text0_0_loc3` = 'Himmelsmeisterin Abendlicht und ihre Drachenfalken halten sich direkt außerhalb der Stadt auf, westlich des Hirtentors.',`Text0_1_loc3` = 'Himmelsmeisterin Abendlicht und ihre Drachenfalken halten sich direkt außerhalb der Stadt auf, westlich des Hirtentors.' WHERE `locales_npc_text`.`ID` = 9323;
UPDATE `locales_npc_text` SET `ID` = 9324,`Text0_0_loc3` = 'Tandrine ist die Gildenmeisterin von Silbermond. Ihr Aufenthaltsort ist an der Straße der Urahnen, nahe des Magistertors.',`Text0_1_loc3` = 'Tandrine ist die Gildenmeisterin von Silbermond. Ihr Aufenthaltsort ist an der Straße der Urahnen, nahe des Magistertors.' WHERE `locales_npc_text`.`ID` = 9324;
UPDATE `locales_npc_text` SET `ID` = 9325,`Text0_0_loc3` = 'Das Gasthaus von Silbermond am königlichen Markt wird von einer talentierten Frau namens Velandra geführt. Es wird gesagt, dass sich dort auch manchmal ein berühmter Kellermeister aufhält.',`Text0_1_loc3` = 'Das Gasthaus von Silbermond am königlichen Markt wird von einer talentierten Frau namens Velandra geführt. Es wird gesagt, dass sich dort auch manchmal ein berühmter Kellermeister aufhält.' WHERE `locales_npc_text`.`ID` = 9325;
UPDATE `locales_npc_text` SET `ID` = 9326,`Text0_0_loc3` = 'Es gibt eine große Zahl von Briefkästen in unserer schönen Stadt. Ich kann Euch den bei der Bank des königlichen Markts empfehlen.',`Text0_1_loc3` = 'Es gibt eine große Zahl von Briefkästen in unserer schönen Stadt. Ich kann Euch den bei der Bank des königlichen Markts empfehlen.' WHERE `locales_npc_text`.`ID` = 9326;
UPDATE `locales_npc_text` SET `ID` = 9327,`Text0_0_loc3` = 'Shalenn kümmert sich um die Tiere in der Stadt und verhindert, dass diese Amok laufen, während ihre Herren hier ihre Zeit verschwenden. Sie steht auf dem Platz der Weltenwanderer, vor der Halle der Waldläufer.',`Text0_1_loc3` = 'Shalenn kümmert sich um die Tiere in der Stadt und verhindert, dass diese Amok laufen, während ihre Herren hier ihre Zeit verschwenden. Sie steht auf dem Platz der Weltenwanderer, vor der Halle der Waldläufer.' WHERE `locales_npc_text`.`ID` = 9327;
UPDATE `locales_npc_text` SET `ID` = 9328,`Text0_0_loc3` = 'Die Blutritter am Platz der Weltenwanderer gewähren in ihren Hallen großzügigerweise einen Platz für Ileda, die Waffenmeisterin von Silbermond. Ihr Unterricht ist nicht billig, aber ihre Erfahrung macht einen Besuch wert.',`Text0_1_loc3` = 'Die Blutritter am Platz der Weltenwanderer gewähren in ihren Hallen großzügigerweise einen Platz für Ileda, die Waffenmeisterin von Silbermond. Ihr Unterricht ist nicht billig, aber ihre Erfahrung macht einen Besuch wert.' WHERE `locales_npc_text`.`ID` = 9328;
UPDATE `locales_npc_text` SET `ID` = 9329,`Text0_0_loc3` = 'Die Kampfmeister halten sich tief in den Hallen der Blutritter am Platz der Weltenwanderer auf. Sie mögen etwas grob sein, aber ihre Talente sind unverzichtbar.',`Text0_1_loc3` = 'Die Kampfmeister halten sich tief in den Hallen der Blutritter am Platz der Weltenwanderer auf. Sie mögen etwas grob sein, aber ihre Talente sind unverzichtbar.' WHERE `locales_npc_text`.`ID` = 9329;
UPDATE `locales_npc_text` SET `ID` = 9330,`Text0_0_loc3` = 'Ein Druide? Unser Volk beschreitet diesen Pfad nicht... aber Ihr habt Glück. Eine Taurenfrau - zumindest glaube ich, dass es eine Frau ist - hat sich eine bescheidene Bleibe am Nordende der Straße der Urahnen eingerichtet. Ihr Name ist Harene Ebenlauf.',`Text0_1_loc3` = 'Ein Druide? Unser Volk beschreitet diesen Pfad nicht... aber Ihr habt Glück. Eine Taurenfrau - zumindest glaube ich, dass es eine Frau ist - hat sich eine bescheidene Bleibe am Nordende der Straße der Urahnen eingerichtet. Ihr Name ist Harene Ebenlauf.' WHERE `locales_npc_text`.`ID` = 9330;
