UPDATE `locales_npc_text` SET `ID` = 9521,`Text0_0_loc3` = 'Es ist unsere Pflicht hier draußen, $GJungchen:Mädel;, uns von niemandem überraschen zu lassen.$B$BUnd das bedeutet, vorbeugend zu handeln!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9521;
