UPDATE `locales_npc_text` SET `ID` = 9523,`Text0_0_loc3` = 'Habt Ihr gehört, dass wir die Kontrolle über Murmur im Schattenlabyrinth komplett verloren haben?',`Text0_1_loc3` = 'Habt Ihr gehört, dass wir die Kontrolle über Murmur im Schattenlabyrinth komplett verloren haben?' WHERE `locales_npc_text`.`ID` = 9523;
UPDATE `locales_npc_text` SET `ID` = 9524,`Text0_0_loc3` = 'Für wen haltet Ihr Euch, dass Ihr einfach hier hinein spaziert und mich ansprecht? Ich bin der Anführer des Schattenrats in diesem Dorf. Geht mir aus den Augen!$B$BWenn ich Euch nochmal sehe, bin ich die letzte Person, die Ihr je gesehen habt, verstanden?$B$BVielleicht wollt Ihr im Labyrinth von Auchindoun begraben oder Murmur geopfert werden?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9524;
