UPDATE `locales_npc_text` SET `ID` = 9575,`Text0_0_loc3` = 'Ich nehme an, dass Ihr keine Neuigkeiten über die Minen habt, oder?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9575;
UPDATE `locales_npc_text` SET `ID` = 9577,`Text0_0_loc3` = 'Hier gibt es nichts zu sehen. Geht weiter.',`Text0_1_loc3` = 'Hier gibt es nichts zu sehen. Geht weiter.' WHERE `locales_npc_text`.`ID` = 9577;
UPDATE `locales_npc_text` SET `ID` = 9578,`Text0_0_loc3` = 'Ich danke Euch, meine Freunde. Dank Euch habe ich meine Freiheit wieder. Wo ist Taretha? Ich hatte gehofft, sie vor meinem Aufbruch in die Berge noch ein letztes Mal sehen und mich bei ihr bedanken zu können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9578;
UPDATE `locales_npc_text` SET `ID` = 9579,`Text0_0_loc3` = 'Was wollt Ihr damit sagen? Befindet sich Taretha in Gefahr?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9579;
UPDATE `locales_npc_text` SET `ID` = 9580,`Text0_0_loc3` = 'Ich werde nichts dergleichen tun. Ich kann Taretha nicht einfach so zurücklassen und in die Berge flüchten. Ich bin kein Feigling. Also, wo ist sie?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9580;
UPDATE `locales_npc_text` SET `ID` = 9583,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9583;
UPDATE `locales_npc_text` SET `ID` = 9584,`Text0_0_loc3` = '<Als Ihr Botschafter Frasaboo in der Sprache der Furbolg begrüßt, lächelt er.>$B$BIhr müsst $Gder:die; sein, von $Gdem:der; in meiner Prophezeiung die Rede war. Es ist mir eine große Ehre, Euch kennenzulernen.$B$BIch bin Botschafter Frasaboo, der Vertreter von Oberhäuptling Tannenruh in der Blutwacht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9584;
UPDATE `locales_npc_text` SET `ID` = 9586,`Text0_0_loc3` = 'In all den Jahren als Mitglied der Forscherliga war ich schon überall in Azeroth, aber nie zuvor habe ich etwas wie die Blutmythosinsel gesehen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9586;
UPDATE `locales_npc_text` SET `ID` = 9587,`Text0_0_loc3` = 'Bitte, redet nicht mit mir. Ich möchte nicht in noch mehr Schwierigkeiten mit denen geraten.',`Text0_1_loc3` = 'Bitte, redet nicht mit mir. Ich möchte nicht in noch mehr Schwierigkeiten mit denen geraten.' WHERE `locales_npc_text`.`ID` = 9587;
UPDATE `locales_npc_text` SET `ID` = 9591,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wir sind gerade erst angekommen, und haben bereits zu viele unserer Freunde und Kameraden begraben müssen.' WHERE `locales_npc_text`.`ID` = 9591;
UPDATE `locales_npc_text` SET `ID` = 9595,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr nicht hier seid, um der Sache zu helfen, verschwindet.' WHERE `locales_npc_text`.`ID` = 9595;
UPDATE `locales_npc_text` SET `ID` = 9596,`Text0_0_loc3` = 'Mit Eurer Hilfe können wir hier erfolgreich sein!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9596;
UPDATE `locales_npc_text` SET `ID` = 9597,`Text0_0_loc3` = 'Tarrens Mühle liegt hinter diesen Bäumen. Das Durcheinander auf Burg Durnholde sollte uns genug Zeit geben, um Taretha zu finden, bevor es hier überall von Soldaten der Menschen nur so wimmelt. Lasst uns zunächst die nahe gelegene Scheune überprüfen, danach geht es weiter in die Stadt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9597;
UPDATE `locales_npc_text` SET `ID` = 9598,`Text0_0_loc3` = 'Ihr könnt bei mir im Austausch gegen Kampfmarken und Forschermarken von Halaa Gegenstände erwerben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9598;
UPDATE `locales_npc_text` SET `ID` = 9599,`Text0_0_loc3` = 'Ich kann Euch die Kunst der Kürschnerei beibringen. Interessiert?',`Text0_1_loc3` = 'Ich kann Euch die Kunst der Kürschnerei beibringen. Interessiert?' WHERE `locales_npc_text`.`ID` = 9599;
UPDATE `locales_npc_text` SET `ID` = 9602,`Text0_0_loc3` = 'Meint Ihr das Gasthaus von Silbermond oder die Taverne Wanderers Ruh''?',`Text0_1_loc3` = 'Meint Ihr das Gasthaus von Silbermond oder die Taverne Wanderers Ruh''?' WHERE `locales_npc_text`.`ID` = 9602;
UPDATE `locales_npc_text` SET `ID` = 9603,`Text0_0_loc3` = 'Die Taverne Wanderers Ruh'' liegt zwischen der Straße der Urahnen und dem Basar. Ihr könnt es von beiden Richtungen aus erreichen.',`Text0_1_loc3` = 'Die Taverne Wanderers Ruh'' liegt zwischen der Straße der Urahnen und dem Basar. Ihr könnt es von beiden Richtungen aus erreichen.' WHERE `locales_npc_text`.`ID` = 9603;
UPDATE `locales_npc_text` SET `ID` = 9610,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Vielen Dank, dass Ihr Thrall bei seiner Flucht geholfen habt, meine Freunde. Nun hoffe ich, dass Ihr auch mir helfen könnt. Ich bin diesen Morgen aufgewacht, nur um mich in diesem magischen Gefängnis wiederzufinden. Ein seltsamer Zauberer sagte mir, ich würde hier eine Weile bleiben müssen.' WHERE `locales_npc_text`.`ID` = 9610;
UPDATE `locales_npc_text` SET `ID` = 9613,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ja, Freunde. Dieser Mann war kein Zauberer aus Dalaran. Seine Kleidung war recht ungewöhnlich und sein Akzent war mir unbekannt. Er redete ständig von Zeit hier und Zeit da. Ihr müsst diesen Mann finden und ihn davon überzeugen, mich freizulassen.' WHERE `locales_npc_text`.`ID` = 9613;
UPDATE `locales_npc_text` SET `ID` = 9614,`Text0_0_loc3` = 'Ich bin froh, dass Taretha noch am Leben ist. Wir müssen nun einen Weg finden, um sie von ihren magischen Fesseln zu befreien.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9614;
UPDATE `locales_npc_text` SET `ID` = 9615,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kam her als Abgesandte des unteren Viertels, um unserem Bestreben Ausdruck zu verleihen, Auchindoun von der Heimsuchung des Schattenrats zu befreien. Das ist notwendig, wenn wir eine Katastrophe verhindern wollen, schlimmer als die Knochenwüste!$B$BUnser Volk hat schon zu viel erleiden müssen. Ich bin hier, um für eine bessere Zukunft zu sorgen!' WHERE `locales_npc_text`.`ID` = 9615;
UPDATE `locales_npc_text` SET `ID` = 9617,`Text0_0_loc3` = 'Nehmt Platz, $GBruder:Schwester;. Die Verheerungen der Zangarmarschen können $Geinem Mann:einer Frau; viel abverlangen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9617;
UPDATE `locales_npc_text` SET `ID` = 9618,`Text0_0_loc3` = 'Wenn Ihr Euch ausruh''n wollt, braucht nur fragen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9618;
UPDATE `locales_npc_text` SET `ID` = 9619,`Text0_0_loc3` = 'Danke für Eure Hilfe bei der Herstellung der Salbe, $N. Sobald sie wirkt und die Schmerzen gelindert sind, werde ich sehen, wie weit ich mit diesem Bein komme.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9619;
UPDATE `locales_npc_text` SET `ID` = 9620,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen in Garadar, $GFremder:Fremde;. Sucht Ihr einen Ort, um Euch auszuruhen?' WHERE `locales_npc_text`.`ID` = 9620;
UPDATE `locales_npc_text` SET `ID` = 9621,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9621;
UPDATE `locales_npc_text` SET `ID` = 9622,`Text0_0_loc3` = 'Ich bin Skaltesh, spiritueller Führer für jene Schamanen, die hier Erleuchtung suchen... doch auch denen, die anderen Pfaden folgen, kann ich das eine oder andere zeigen. Wünscht Ihr zu sehen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9622;
UPDATE `locales_npc_text` SET `ID` = 9623,`Text0_0_loc3` = 'Trinkt von dieser Phiole und der Nebel wird sich für kurze Zeit lüften. Geht mit den Geistern, $Gmein Freund:meine Freundin;.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9623;
UPDATE `locales_npc_text` SET `ID` = 9624,`Text0_0_loc3` = 'Sylann, eine der besten Köchinnen der Stadt, hat ein Zimmer im ersten Stock der Taverne Wanderers Ruh''. Ihr könnt dort auch Kochbedarfswaren von ihrem Assistenten erwerben.',`Text0_1_loc3` = 'Sylann, eine der besten Köchinnen der Stadt, hat ein Zimmer im ersten Stock der Taverne Wanderers Ruh''. Ihr könnt dort auch Kochbedarfswaren von ihrem Assistenten erwerben.' WHERE `locales_npc_text`.`ID` = 9624;
UPDATE `locales_npc_text` SET `ID` = 9625,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9625;
UPDATE `locales_npc_text` SET `ID` = 9626,`Text0_0_loc3` = 'Während dem Winterhauchfest versteckt Altvater Winter für alle Spielzeuge unter dem Baum in Eisenschmiede.',`Text0_1_loc3` = 'Während dem Winterhauchfest versteckt Altvater Winter für alle Spielzeuge unter dem Baum in Eisenschmiede.' WHERE `locales_npc_text`.`ID` = 9626;
UPDATE `locales_npc_text` SET `ID` = 9630,`Text0_0_loc3` = 'Meine Schwester Silanna ist den Gefahren des Waldes gegenüber blauäugig. Aber ob sie es mag oder nicht, sobald ich diesen Wagen vollgeladen habe, ziehen wir in die Stadt.$B$BEs scheint, als hätte ich viel mehr Taschen, als ich brauche. Vielleicht könnt Ihr sie ja sinnvoll einsetzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9630;
UPDATE `locales_npc_text` SET `ID` = 9631,`Text0_0_loc3` = 'Ich habe nicht viel Zeit für Geschwätz, $R. Oder wollt Ihr mir mit meinem Problemchen hier helfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9631;
UPDATE `locales_npc_text` SET `ID` = 9632,`Text0_0_loc3` = 'Kann Euch nicht genug dafür danken, dass Ihr mir mit den Minen geholfen habt. Nun müssen nur noch die Minenarbeiter überzeugt werden, wieder an die Arbeit zu gehen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9632;
UPDATE `locales_npc_text` SET `ID` = 9634,`Text0_0_loc3` = '<Isfar seufzt.>$B$BEin paar Baumhäuser sind noch lange kein Zuhause, aber schon viel besser als die Sethekkhallen.$B$BSo bequem es in Shattrath war, kann ich es nicht über mich bringen, ohne meine Schwester nach Skettis zurückzukehren. Sie ist immer noch in den Sethekkhallen gefangen, festgehalten von unserem älteren Bruder.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9634;
UPDATE `locales_npc_text` SET `ID` = 9636,`Text0_0_loc3` = 'Jetzt ist unsere Chance! Lasst mich aus dem Käfig raus!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9636;
UPDATE `locales_npc_text` SET `ID` = 9637,`Text0_0_loc3` = 'Der Ring der Prüfung war einst ein Ort der Ehre und Würde. Aber dann kam das Dampfdruckkartell mit ihren Wagen voller Gold und Federn, die schärfer waren, als jede Orcaxt...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9637;
UPDATE `locales_npc_text` SET `ID` = 9638,`Text0_0_loc3` = 'Die Sethekk glauben, dass sie die wahren und treuesten Anhänger des Meisters aller Arakkoa sind. Fragt mich nicht nach dem Meister, $R, denn seine Geheimnisse sind nur für das gefiederte Volk bestimmt.$B$BDie Sethekk haben die Zerstörung Auchindouns als Ankunft des Meisters auf diesem Planeten gedeutet. Sie sind zu den Ruinen aufgebrochen und haben die Relikte unseres größten Helden, Terokk, mit sich genommen.$B$BSie suchen in den Ruinen immer noch nach unserem Meister, aber ich habe ihre Lügen durchschaut. Ich gehöre nicht mehr zu ihnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9638;
UPDATE `locales_npc_text` SET `ID` = 9639,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9639;
UPDATE `locales_npc_text` SET `ID` = 9640,`Text0_0_loc3` = 'Der Klauenkönig Ikiss ist einer der Anführer der Sethekk, zusammen mit meinem Bruder, Dunkelwirker Syth.$B$BSie haben den Auszug der Sethekk aus Skettis zu den Ruinen von Auchindoun angeführt. Zu Anfang war Ikiss ein sehr charismatischer Führer, aber er ist dem Wahnsinn verfallen.$B$BNun gibt er öffentlich bekannt, dass Terokk wiedergeboren wurde! Er denkt wirklich, dass die Arakkoa sich ihm als Führer unterwerfen! Könnt Ihr diesen Hochmut fassen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9640;
UPDATE `locales_npc_text` SET `ID` = 9641,`Text0_0_loc3` = 'Er ist der größte Held, den die Arakkoa je kannten.$B$BMan sagt, dass er in alten Zeiten die Stadt Skettis mit seinen eigenen Klauen erbaut hat. Er war unser größter Champion und Verteidiger, aber er verließ uns, als wir aufhörten, Rukhmar zu verehren.$B$BWir haben begonnen, einem neuen Meister zu dienen, der uns ungeahnte Kräfte und Privilegien im Kosmos versprach.$B$BEinige sagen, dass Rukhmar und Terokk ein und dieselbe Person sind, und er uns verlassen hat, als wir ihm den Rücken kehrten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9641;
UPDATE `locales_npc_text` SET `ID` = 9642,`Text0_0_loc3` = 'Nur Muskeln, kein Hirn, das sind die Peons! Sie sind großartig, wenn sie motiviert sind, aber sie brauchen einen An-Tritt um loszulegen, wenn Ihr versteht, was ich meine.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9642;
UPDATE `locales_npc_text` SET `ID` = 9643,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gefällt Euch etwas, von dem, was Ihr seht?' WHERE `locales_npc_text`.`ID` = 9643;
UPDATE `locales_npc_text` SET `ID` = 9644,`Text0_0_loc3` = 'Wenn Ihr Geld habt, habe ich, was Ihr braucht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9644;
UPDATE `locales_npc_text` SET `ID` = 9645,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seht Euch ruhig um und lasst es mich wissen, wenn Ihr etwas gefunden habt, das Euch gefällt.' WHERE `locales_npc_text`.`ID` = 9645;
UPDATE `locales_npc_text` SET `ID` = 9646,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Bitte, nehmt Euch ruhig Zeit und betrachtet die Waffen in aller Ruhe. Ich repariere auch Waffen und Rüstungen, falls Ihr derartige Dienste benötigt.' WHERE `locales_npc_text`.`ID` = 9646;
UPDATE `locales_npc_text` SET `ID` = 9647,`Text0_0_loc3` = 'Ich habe genau das, was Ihr für Euren Zauber braucht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9647;
UPDATE `locales_npc_text` SET `ID` = 9648,`Text0_0_loc3` = 'Um in Nagrand Ruhe und Frieden wiederherzustellen, braucht es eine scharfe Klinge und verstärkte Rüstung. Soll ich Eure Ausrüstung reparieren, damit Ihr Gerechtigkeit walten lassen könnt?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9648;
UPDATE `locales_npc_text` SET `ID` = 9649,`Text0_0_loc3` = 'Sucht Ihr Reagenzien oder Zutaten für Gifte? Dann seid Ihr hier richtig!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9649;
UPDATE `locales_npc_text` SET `ID` = 9650,`Text0_0_loc3` = 'Ich verkaufe Waren für Handwerker. Nur die Grundlagen, nichts Ausgefallenes.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9650;
UPDATE `locales_npc_text` SET `ID` = 9651,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Sucht Ihr einen Platz für Euer Tier?' WHERE `locales_npc_text`.`ID` = 9651;
UPDATE `locales_npc_text` SET `ID` = 9652,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Habt Ihr Euch verlaufen? Die meisten Händler befinden sich in der Stadtmitte.' WHERE `locales_npc_text`.`ID` = 9652;
UPDATE `locales_npc_text` SET `ID` = 9653,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kann Euch Krimskrams verkaufen, oder auch nur Krams.' WHERE `locales_npc_text`.`ID` = 9653;
UPDATE `locales_npc_text` SET `ID` = 9654,`Text0_0_loc3` = 'Es treibt mich in den Wahnsinn. Ich kann diese verdammten Eier nicht erreichen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9654;
UPDATE `locales_npc_text` SET `ID` = 9656,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Jahrelang war die Ehrenfeste die letzte Bastion gegen die Schrecken der Höllenfeuerhalbinsel. Doch jetzt, wo sich das Dunkle Portal erneut geöffnet hat und beständig Entsatz aus Azeroth eintrifft, findet dieser blutige Kampf vielleicht endlich ein Ende.' WHERE `locales_npc_text`.`ID` = 9656;
UPDATE `locales_npc_text` SET `ID` = 9657,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist bedauerlich, dass sich unter all unseren Feinden auf der Höllenfeuerhalbinsel auch die Orcs der Höllenfeuerzitadelle befinden.$B$BDoch wer vermag die Horde besser herauszufordern als... Orcs!' WHERE `locales_npc_text`.`ID` = 9657;
UPDATE `locales_npc_text` SET `ID` = 9659,`Text0_0_loc3` = 'Gut, Euch wiederzusehen, $N. Nur ein paar Feineinstellungen mehr, und die Richtung des Sprungs sollte perfekt sein!',`Text0_1_loc3` = 'Gut, Euch wiederzusehen, $N. Nur ein paar Feineinstellungen mehr, und die Richtung des Sprungs sollte perfekt sein!' WHERE `locales_npc_text`.`ID` = 9659;
UPDATE `locales_npc_text` SET `ID` = 9662,`Text0_0_loc3` = 'Die Feierlichkeiten des Mondfests werden von den Druiden auf der Mondlichtung abgehalten. Wenn Euch die Reise zu lang ist, geht zum Mondbrunnen im Park. Dort gibt es eine schnelle Transportmöglichkeit zur Lichtung.',`Text0_1_loc3` = 'Die Feierlichkeiten des Mondfests werden von den Druiden auf der Mondlichtung abgehalten. Wenn Euch die Reise zu lang ist, geht zum Mondbrunnen im Park. Dort gibt es eine schnelle Transportmöglichkeit zur Lichtung.' WHERE `locales_npc_text`.`ID` = 9662;
UPDATE `locales_npc_text` SET `ID` = 9686,`Text0_0_loc3` = 'Ihr findet Altvater Winter vor der Bank von Eisenschmiede. Geschenke werden beim Baum in seiner Nähe verteilt.',`Text0_1_loc3` = 'Ihr findet Altvater Winter vor der Bank von Eisenschmiede. Geschenke werden beim Baum in seiner Nähe verteilt.' WHERE `locales_npc_text`.`ID` = 9686;
UPDATE `locales_npc_text` SET `ID` = 9688,`Text0_0_loc3` = 'Geht in das Mystikerviertel von Eisenschmiede und sprecht dort mit den Druiden der Nachtelfen. Sie können Euch in null Komma nichts zur Mondlichtung transportieren.',`Text0_1_loc3` = 'Geht in das Mystikerviertel von Eisenschmiede und sprecht dort mit den Druiden der Nachtelfen. Sie können Euch in null Komma nichts zur Mondlichtung transportieren.' WHERE `locales_npc_text`.`ID` = 9688;
UPDATE `locales_npc_text` SET `ID` = 9692,`Text0_0_loc3` = 'Auf der Mondlichtung sind die Festivitäten des Mondfests in vollem Gange. Ihr könnt Euch zu Fuß auf den Weg machen, oder die Druiden in der Enklave des Cenarius aufsuchen. Sie verfügen über die Fähigkeit, Euch direkt auf die Mondlichtung zu teleportieren.',`Text0_1_loc3` = 'Auf der Mondlichtung sind die Festivitäten des Mondfests in vollem Gange. Ihr könnt Euch zu Fuß auf den Weg machen, oder die Druiden in der Enklave des Cenarius aufsuchen. Sie verfügen über die Fähigkeit, Euch direkt auf die Mondlichtung zu teleportieren.' WHERE `locales_npc_text`.`ID` = 9692;
