UPDATE `locales_npc_text` SET `ID` = 9696,`Text0_0_loc3` = 'Die Druiden von Azeroth halten ein großes Fest auf der Mondlichtung ab. Sucht in den Städten nach den Feiernden des Mondfests, sie können Euch dorthin transportieren.',`Text0_1_loc3` = 'Die Druiden von Azeroth halten ein großes Fest auf der Mondlichtung ab. Sucht in den Städten nach den Feiernden des Mondfests, sie können Euch dorthin transportieren.' WHERE `locales_npc_text`.`ID` = 9696;
UPDATE `locales_npc_text` SET `ID` = 9698,`Text0_0_loc3` = 'Wenn Ihr das Winterhauchfest in all seiner Herrlichkeit erleben möchtet, solltet Ihr nach Orgrimmar gehen.',`Text0_1_loc3` = 'Wenn Ihr das Winterhauchfest in all seiner Herrlichkeit erleben möchtet, solltet Ihr nach Orgrimmar gehen.' WHERE `locales_npc_text`.`ID` = 9698;
UPDATE `locales_npc_text` SET `ID` = 9700,`Text0_0_loc3` = 'Falls Ihr die Feiernden des Mondfests in Silbermond findet, können sie Euch zur Mondlichtung transportieren, damit Ihr die Feierlichkeiten dort genießen könnt.',`Text0_1_loc3` = 'Falls Ihr die Feiernden des Mondfests in Silbermond findet, können sie Euch zur Mondlichtung transportieren, damit Ihr die Feierlichkeiten dort genießen könnt.' WHERE `locales_npc_text`.`ID` = 9700;
