UPDATE `locales_npc_text` SET `ID` = 9704,`Text0_0_loc3` = 'Die größten Feierlichkeiten des Mondfests finden auf der Mondlichtung statt. Im Tal der Weisheit gibt es einige Druiden, die, falls Euch die Reise zu lang ist, Euch direkt dorthin teleportieren können.',`Text0_1_loc3` = 'Die größten Feierlichkeiten des Mondfests finden auf der Mondlichtung statt. Im Tal der Weisheit gibt es einige Druiden, die, falls Euch die Reise zu lang ist, Euch direkt dorthin teleportieren können.' WHERE `locales_npc_text`.`ID` = 9704;
UPDATE `locales_npc_text` SET `ID` = 9706,`Text0_0_loc3` = 'Die Feierlichkeiten des Winterhauchfests sind ein Quell der Wärme in dieser ach so kalten Jahreszeit, und Orgrimmar ist der Dreh- und Angelpunkt dieser wundervollen Tradition.',`Text0_1_loc3` = 'Die Feierlichkeiten des Winterhauchfests sind ein Quell der Wärme in dieser ach so kalten Jahreszeit, und Orgrimmar ist der Dreh- und Angelpunkt dieser wundervollen Tradition.' WHERE `locales_npc_text`.`ID` = 9706;
UPDATE `locales_npc_text` SET `ID` = 9710,`Text0_0_loc3` = 'Noch immer richten wir uns nach den alten Traditionen, obwohl die Seele sich während des Winterhauchfests leer anfühlt. Doch in Orgrimmar, da finden die wahren Feierlichkeiten statt.',`Text0_1_loc3` = 'Noch immer richten wir uns nach den alten Traditionen, obwohl die Seele sich während des Winterhauchfests leer anfühlt. Doch in Orgrimmar, da finden die wahren Feierlichkeiten statt.' WHERE `locales_npc_text`.`ID` = 9710;
UPDATE `locales_npc_text` SET `ID` = 9712,`Text0_0_loc3` = 'Dort wo einst die Sonne in unseren Herzen schien, hat nun der Mond ihren Platz eingenommen. Solltet Ihr Euch dennoch dazu entschließen, an den Feierlichkeiten teilzunehmen, begebt Euch zur Mondlichtung in Kalimdor. Im Thronsaal über der Unterstadt findet Ihr einige Druiden, die Euch direkt dorthin transportieren können.',`Text0_1_loc3` = 'Dort wo einst die Sonne in unseren Herzen schien, hat nun der Mond ihren Platz eingenommen. Solltet Ihr Euch dennoch dazu entschließen, an den Feierlichkeiten teilzunehmen, begebt Euch zur Mondlichtung in Kalimdor. Im Thronsaal über der Unterstadt findet Ihr einige Druiden, die Euch direkt dorthin transportieren können.' WHERE `locales_npc_text`.`ID` = 9712;
UPDATE `locales_npc_text` SET `ID` = 9713,`Text0_0_loc3` = 'Ihr da - kommt her. Sind diese Klingen nicht von bester Qualität? Ich wollt sicher von mir kaufen. Nirgends sonst findet Ihr Waffen, die mit solcher Kunstfertigkeit und Hingabe geschmiedet wurden, $R.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 9713;
