UPDATE `locales_npc_text` SET `ID` = 10089,`Text0_0_loc3` = 'Wir haben hier in der Azurwacht keine Schmiede. Ich habe jedoch ein paar junge Schmiede aus diesem merkwürdigen Lager an der südlichen Küste kommen sehen. Vielleicht solltet Ihr dort nachschauen.',`Text0_1_loc3` = 'Wir haben hier in der Azurwacht keine Schmiede. Ich habe jedoch ein paar junge Schmiede aus diesem merkwürdigen Lager an der südlichen Küste kommen sehen. Vielleicht solltet Ihr dort nachschauen.' WHERE `locales_npc_text`.`ID` = 10089;
UPDATE `locales_npc_text` SET `ID` = 10090,`Text0_0_loc3` = 'Wir können keine Köche entbehren, aber eines ist merkwürdig... vom Süden her riecht es sehr appetitlich. Folgt einfach Eurer Nase, ja?',`Text0_1_loc3` = 'Wir können keine Köche entbehren, aber eines ist merkwürdig... vom Süden her riecht es sehr appetitlich. Folgt einfach Eurer Nase, ja?' WHERE `locales_npc_text`.`ID` = 10090;
