UPDATE `locales_npc_text` SET `ID` = 10093,`Text0_0_loc3` = 'Fateema ist unser Anachoret. Bei der Versorgung der Absturzopfer war sie uns eine unschätzbare Hilfe. Sie übt ihr Handwerk auf der westlichen Seite der Wacht aus.',`Text0_1_loc3` = 'Fateema ist unser Anachoret. Bei der Versorgung der Absturzopfer war sie uns eine unschätzbare Hilfe. Sie übt ihr Handwerk auf der westlichen Seite der Wacht aus.' WHERE `locales_npc_text`.`ID` = 10093;
