UPDATE `locales_npc_text` SET `ID` = 10095,`Text0_0_loc3` = 'Heur hat seit unserer Ankunft die Pflanzenwelt dieser Gegend erkundet. Er wird Euch etwas beibringen können. Ihr findet ihn bei der Arbeit zusammen mit Daedal am westlichen Ende der Azurwacht.',`Text0_1_loc3` = 'Heur hat seit unserer Ankunft die Pflanzenwelt dieser Gegend erkundet. Er wird Euch etwas beibringen können. Ihr findet ihn bei der Arbeit zusammen mit Daedal am westlichen Ende der Azurwacht.' WHERE `locales_npc_text`.`ID` = 10095;
UPDATE `locales_npc_text` SET `ID` = 10096,`Text0_0_loc3` = 'Unsere neuen Verbündeten, die Furbolgs der Tannenruhfeste, haben ihr Interesse daran bekundet, uns alle Häute abzunehmen. Sie leben im Norden. Sucht bei ihnen nach Wissen.',`Text0_1_loc3` = 'Unsere neuen Verbündeten, die Furbolgs der Tannenruhfeste, haben ihr Interesse daran bekundet, uns alle Häute abzunehmen. Sie leben im Norden. Sucht bei ihnen nach Wissen.' WHERE `locales_npc_text`.`ID` = 10096;
UPDATE `locales_npc_text` SET `ID` = 10097,`Text0_0_loc3` = 'Dulvi ist die Frau, nach der Ihr sucht. Sie bildet neue Talente in der Mitte der Wacht aus.',`Text0_1_loc3` = 'Dulvi ist die Frau, nach der Ihr sucht. Sie bildet neue Talente in der Mitte der Wacht aus.' WHERE `locales_npc_text`.`ID` = 10097;
UPDATE `locales_npc_text` SET `ID` = 10098,`Text0_0_loc3` = 'Reist zur Tannenruhfeste, mein Freund. Die Furbolgs dort waren sehr hilfreich. Vielleicht haben sie genau das Wissen, das Ihr braucht.',`Text0_1_loc3` = 'Reist zur Tannenruhfeste, mein Freund. Die Furbolgs dort waren sehr hilfreich. Vielleicht haben sie genau das Wissen, das Ihr braucht.' WHERE `locales_npc_text`.`ID` = 10098;
UPDATE `locales_npc_text` SET `ID` = 10099,`Text0_0_loc3` = 'Wir haben hier in der Azurwacht keine Schneider. Ihr könnt es jedoch bei unserem Lager am Strand versuchen. Dort soll es eine Reihe von Handwerkern von jenseits des Meeres geben.',`Text0_1_loc3` = 'Wir haben hier in der Azurwacht keine Schneider. Ihr könnt es jedoch bei unserem Lager am Strand versuchen. Dort soll es eine Reihe von Handwerkern von jenseits des Meeres geben.' WHERE `locales_npc_text`.`ID` = 10099;
UPDATE `locales_npc_text` SET `ID` = 10100,`Text0_0_loc3` = 'Sucht Ihr nach einem Edelsteinschneider? Unsere Juwelenschleifer befinden sich in der Exodar und bewirken wahre Wunder mit den Materialien, die sie gefunden haben. Sprecht mit Farii, wenn Ihr dort seid.',`Text0_1_loc3` = 'Sucht Ihr nach einem Edelsteinschneider? Unsere Juwelenschleifer befinden sich in der Exodar und bewirken wahre Wunder mit den Materialien, die sie gefunden haben. Sprecht mit Farii, wenn Ihr dort seid.' WHERE `locales_npc_text`.`ID` = 10100;
UPDATE `locales_npc_text` SET `ID` = 10101,`Text0_0_loc3` = 'Was wollt Ihr?! Ich habe etwas von diesen merkwürdigen Draeneikreaturen im Tal der Helden gehört. Vielleicht weiß sie etwas darüber. Ich nicht.',`Text0_1_loc3` = 'Was wollt Ihr?! Ich habe etwas von diesen merkwürdigen Draeneikreaturen im Tal der Helden gehört. Vielleicht weiß sie etwas darüber. Ich nicht.' WHERE `locales_npc_text`.`ID` = 10101;
UPDATE `locales_npc_text` SET `ID` = 10103,`Text0_0_loc3` = 'Der Geist des Wassers hat uns verlassen. Unser Stamm ist verloren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10103;
UPDATE `locales_npc_text` SET `ID` = 10104,`Text0_0_loc3` = 'Die Winde sprechen nicht mehr zu meinem Stamm, $N. Meine Leute sind ohne die Ältesten verloren. Nur sie können die Geister zu meinem Stamm zurückbringen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10104;
UPDATE `locales_npc_text` SET `ID` = 10105,`Text0_0_loc3` = 'Die Fesseln, welche mich gefangen halten, hindern mich daran, meinem Volk den Geist des Feuers zu bringen, $N. Seelen ohne Feuer sind zu schwach, um gegen Ihre Herren aufzubegehren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10105;
UPDATE `locales_npc_text` SET `ID` = 10106,`Text0_0_loc3` = 'Ich weiß nicht, was in Euch gefahren ist, einen Schamanen zu suchen... aber ich habe jemanden gesehen, der Euch weiterhelfen könnte. Am Ufer im Tal der Helden gibt es eine Frau, die sich Weissagerin nennt.',`Text0_1_loc3` = 'Ich weiß nicht, was in Euch gefahren ist, einen Schamanen zu suchen... aber ich habe jemanden gesehen, der Euch weiterhelfen könnte. Am Ufer im Tal der Helden gibt es eine Frau, die sich Weissagerin nennt.' WHERE `locales_npc_text`.`ID` = 10106;
UPDATE `locales_npc_text` SET `ID` = 10107,`Text0_0_loc3` = 'Wir nicht mögen Paladine. Elfendame kommen in Stadt, bleiben in Königsviertel. Ihr reden, lernen Paladinsachen.',`Text0_1_loc3` = 'Wir nicht mögen Paladine. Elfendame kommen in Stadt, bleiben in Königsviertel. Ihr reden, lernen Paladinsachen.' WHERE `locales_npc_text`.`ID` = 10107;
