UPDATE `locales_npc_text` SET `ID` = 10153,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Kael''thas zu verlassen war die beste Entscheidung unseres Lebens. Seine Gefolgsleute sind zu blind oder zu ängstlich, zu erkennen, wohin er sie führt.$B$BWir hingegen sind das nicht.' WHERE `locales_npc_text`.`ID` = 10153;
