UPDATE `locales_npc_text` SET `ID` = 10220,`Text0_0_loc3` = 'Geht zur Anhöhe der Jäger. Dort findet Ihr die Kampfmeister.',`Text0_1_loc3` = 'Geht zur Anhöhe der Jäger. Dort findet Ihr die Kampfmeister.' WHERE `locales_npc_text`.`ID` = 10220;
UPDATE `locales_npc_text` SET `ID` = 10221,`Text0_0_loc3` = 'Ihr findet die Kampfmeister in der Halle der Kriegerhelden im Tal der Ehre.',`Text0_1_loc3` = 'Ihr findet die Kampfmeister in der Halle der Kriegerhelden im Tal der Ehre.' WHERE `locales_npc_text`.`ID` = 10221;
UPDATE `locales_npc_text` SET `ID` = 10222,`Text0_0_loc3` = 'Geht ins Königsviertel. Sie stehen dort alle im Kreis.',`Text0_1_loc3` = 'Geht ins Königsviertel. Sie stehen dort alle im Kreis.' WHERE `locales_npc_text`.`ID` = 10222;
UPDATE `locales_npc_text` SET `ID` = 10223,`Text0_0_loc3` = 'Ihr findet ihn im Versorgungszentrum im Sitz der Naaru. Das Licht sei mit Euch.',`Text0_1_loc3` = 'Ihr findet ihn im Versorgungszentrum im Sitz der Naaru. Das Licht sei mit Euch.' WHERE `locales_npc_text`.`ID` = 10223;
UPDATE `locales_npc_text` SET `ID` = 10224,`Text0_0_loc3` = 'Oh ja, ich habe Krapfen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10224;
UPDATE `locales_npc_text` SET `ID` = 10229,`Text0_0_loc3` = 'Fleischling, ich bin Hauptmann Saeed des Protektorats, und die Soldaten, die hier bei mir stehen, sind meine Rächer. Wir erwarten Befehle von dem Fleischling, der die Leerversorgung zerstört hat. Auf Befehl dieses Fleischlings werden wir uns zu Dimensius'' Lager begeben, und auf seine Aufforderung hin einen letzten Angriff starten!$B$BEs dauert schon viel zu lange... Der Fürst der Leere wird bald unsere vereinte Kraft zu spüren bekommen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10229;
UPDATE `locales_npc_text` SET `ID` = 10230,`Text0_0_loc3` = 'Der Netherdrache schaut Euch prüfend an.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10230;
UPDATE `locales_npc_text` SET `ID` = 10232,`Text0_0_loc3` = 'Da sind wir, $N. Wir greifen auf Euren Befehl hin an. Sagt Bescheid, und ich und meine Männer werden uns in den Kampf stürzen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10232;
UPDATE `locales_npc_text` SET `ID` = 10233,`Text0_0_loc3` = 'Willkommen in der Biokuppel Mittelreich, Reisender. Ich möchte Euch davor warnen, Geschäfte mit diesem Goblin Shauly zu treiben. Ich habe wohl nie zuvor jemanden gehört, der das Wort "Kumpel" als Satzzeichen verwendet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10233;
UPDATE `locales_npc_text` SET `ID` = 10234,`Text0_0_loc3` = 'Da Allerias Feste auf allen Seiten von Feinden umgeben ist, ist es an der Zeit, sie zu bekämpfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10234;
UPDATE `locales_npc_text` SET `ID` = 10236,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr hierher kommt, macht Euch nützlich, denn der Schattenpirscher da drüben macht nicht sehr viel.' WHERE `locales_npc_text`.`ID` = 10236;
UPDATE `locales_npc_text` SET `ID` = 10237,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Schlacht um den Berg Hyjal ist eines der bestgehüteten Ereignisse dieser Zeitlinie. Sollte ein Eindringling ihren Ausgang ändern, hätte dies Auswirkungen auf alle folgenden Momente in der Geschichte.$B$BAls Hauptgefährtin Nozdormus bin ich viel mehr im Einklang mit dem Strom der Zeit als die anderen Bronzedrachen. Vor kurzem spürte ich eine Welle in der Zeit, die von den Ereignissen bei der Schlacht um den Berg Hyjal herrührte. Jemand oder etwas versucht, diese Zeitlinie zu verändern. Das muss verhindert werden!' WHERE `locales_npc_text`.`ID` = 10237;
UPDATE `locales_npc_text` SET `ID` = 10239,`Text0_0_loc3` = 'Was wollt Ihr? Sprecht. Ich vertraue meine Elekk niemandem an, der nicht zuvor seine Hingabe und Freundschaft zu unserem Volk bewiesen hat.',`Text0_1_loc3` = 'Was wollt Ihr? Sprecht. Ich vertraue meine Elekk niemandem an, der nicht zuvor seine Hingabe und Freundschaft zu unserem Volk bewiesen hat.' WHERE `locales_npc_text`.`ID` = 10239;
UPDATE `locales_npc_text` SET `ID` = 10245,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Unsere Arbeit im lebenden Hain darf nicht gefährdet werden. Unser Leben hängt davon ab, dass er gesund bleibt und wächst.' WHERE `locales_npc_text`.`ID` = 10245;
UPDATE `locales_npc_text` SET `ID` = 10246,`Text0_0_loc3` = 'Falls Ihr noch nicht bei ihr wart, bietet bitte Rina Mondquell eure Hilfe an.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10246;
UPDATE `locales_npc_text` SET `ID` = 10247,`Text0_0_loc3` = 'Wenn ich recht gehört habe, leistet Ihr bereits gute Arbeit für Sylvanaar.$B$BMacht weiter so, und vielleicht werden wir in der Lage sein, unser Heim hier zu behalten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10247;
UPDATE `locales_npc_text` SET `ID` = 10249,`Text0_0_loc3` = 'Geht und ruht Euch aus, Wanderer. Unsere Feuer sind Eure Feuer.',`Text0_1_loc3` = 'Geht und ruht Euch aus, Wanderer. Unsere Feuer sind Eure Feuer.' WHERE `locales_npc_text`.`ID` = 10249;
UPDATE `locales_npc_text` SET `ID` = 10250,`Text0_0_loc3` = 'Hier durchtrennte Gul''dan die Verbindung zwischen den Orcs und den Elementargeistern. Sein unstillbarer Durst nach Macht konnte durch die Auslöschung der Draenei nicht gesättigt werden. Er musste außerdem noch Draenor zerstören, das Land auslöschen und dessen Energien für seinen Krieg absaugen.$B$BAlles, was bleibt, sind Überreste seines Wahnsinns.$B$BSchaut zum Altar, $R: Das Land ist für immer verflucht...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10250;
UPDATE `locales_npc_text` SET `ID` = 10251,`Text0_0_loc3` = 'Zu Ehren meiner Vorfahren übe ich die alte Tradition der Schamanen aus. Wir dürfen niemals die Opfer vergessen, die sie gebracht haben, damit wir heute hier sein können.$B$BUnd im Gegenzug müssen wir die notwendigen Opfer bringen, damit unsere Nachfahren eine Zukunft haben.$B$BWas bringt Euch zu mir, $C?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10251;
UPDATE `locales_npc_text` SET `ID` = 10252,`Text0_0_loc3` = 'Vor nicht allzu langer Zeit haben wir den nahen Ogern die Kontrolle über die Donnerfeste abgerungen.$B$BSeid Ihr hier, um uns bei der Verteidigung zu helfen? Ich weiß, dass Gor''drek nach Unterstützung sucht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10252;
UPDATE `locales_npc_text` SET `ID` = 10253,`Text0_0_loc3` = 'Wie findet Ihr den Krieg, $N? Denkt immer daran, warum wir hier sind, das macht das Kämpfen einfacher... und das Sterben auch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10253;
UPDATE `locales_npc_text` SET `ID` = 10254,`Text0_0_loc3` = 'Es gibt am Sitz der Naaru zwei Briefkästen: einen bei der Bank, den anderen am Auktionshaus.',`Text0_1_loc3` = 'Es gibt am Sitz der Naaru zwei Briefkästen: einen bei der Bank, den anderen am Auktionshaus.' WHERE `locales_npc_text`.`ID` = 10254;
UPDATE `locales_npc_text` SET `ID` = 10255,`Text0_0_loc3` = 'Beim Expeditionsposten stationiert zu sein, ist ein hartes Los, aber so wichtig zur Verteidigung der Ehrenfeste. Die Informationen, die dort gesammelt werden, und der Einsatz unserer Soldaten gegen die Legion und die Orcs von Zeth''Gor sind von unschätzbarem Wert für die Pläne der Allianz auf der Höllenfeuerhalbinsel.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10255;
UPDATE `locales_npc_text` SET `ID` = 10256,`Text0_0_loc3` = 'Ich höre, dass Ihr Euch hier bereits nützlich macht.$B$BSehr gut. Ich hatte mir schon Sorgen gemacht, wir müssten Euch ''überzeugen'', hier auszuhelfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10256;
UPDATE `locales_npc_text` SET `ID` = 10257,`Text0_0_loc3` = 'Auf der Haut, die zwischen den Knochen straff gespannt ist, steht ein Aufruf gegen die wichtigsten Verteidiger von Sylvanaar und dem lebenden Hain im Westen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10257;
UPDATE `locales_npc_text` SET `ID` = 10258,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt, ich bin Azimi! Ich gehe oft hier entlang und verkaufe den Männern frische Früchte und Wasser. Seid Ihr hungrig, oder vielleicht durstig?' WHERE `locales_npc_text`.`ID` = 10258;
UPDATE `locales_npc_text` SET `ID` = 10259,`Text0_0_loc3` = 'Bah! Sehe ich aus, als hätte ich Zeit für faules Geschwätz?$B$BSeht zu, dass Ihr fertig werdet, es warten Oger darauf, getötet zu werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10259;
UPDATE `locales_npc_text` SET `ID` = 10264,`Text0_0_loc3` = 'Es tut gut zu sehen, dass $Gein:eine:r; $R wie Ihr zum Wohlergehen von Sylvanaar beiträgt. Wir haben hier in kurzer Zeit viel geschafft, aber wir sind von Streitkräften umgeben, die unsere Art zu leben bedrohen.$B$BIch möchte hören, was Ihr zu sagen habt. Sprecht, $C.',`Text0_1_loc3` = 'Es tut gut zu sehen, dass $Gein:eine:r; $R wie Ihr zum Wohlergehen von Sylvanaar beiträgt. Wir haben hier in kurzer Zeit viel geschafft, aber wir sind von Streitkräften umgeben, die unsere Art zu leben bedrohen.$B$BIch möchte hören, was Ihr zu sagen habt. Sprecht, $C.' WHERE `locales_npc_text`.`ID` = 10264;
UPDATE `locales_npc_text` SET `ID` = 10266,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen in der Zuflucht des Cenarius, $N. Ich wurde angewiesen, Euch mit Grundlegendem zu versorgen, solltet Ihr etwas benötigen.' WHERE `locales_npc_text`.`ID` = 10266;
UPDATE `locales_npc_text` SET `ID` = 10269,`Text0_0_loc3` = 'Seid Ihr bereit, im Alteractal gegen die Sturmlanzengarde zu kämpfen, $N?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10269;
UPDATE `locales_npc_text` SET `ID` = 10270,`Text0_0_loc3` = 'Bronwyn und ich sind Abgesandte der Forscherliga. Wir sind hier auf Geheiß der Allianz, um diese Berge zu studieren.$B$BAber unter uns, nach einem langen Aufenthalt in Silithus brauchen wir einfach etwas Urlaub. Dieser Ort scheint ganz nett zu sein.',`Text0_1_loc3` = 'Bronwyn und ich sind Abgesandte der Forscherliga. Wir sind hier auf Geheiß der Allianz, um diese Berge zu studieren.$B$BAber unter uns, nach einem langen Aufenthalt in Silithus brauchen wir einfach etwas Urlaub. Dieser Ort scheint ganz nett zu sein.' WHERE `locales_npc_text`.`ID` = 10270;
UPDATE `locales_npc_text` SET `ID` = 10271,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Borgrim könnte Recht haben. Vielleicht brauchen wir eine Pause.$B$BAber jetzt, wo wir hier sind, reizt es mich, anzupacken und dieses Land zu studieren.' WHERE `locales_npc_text`.`ID` = 10271;
UPDATE `locales_npc_text` SET `ID` = 10272,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10272;
UPDATE `locales_npc_text` SET `ID` = 10275,`Text0_0_loc3` = 'Die Söhne Lothars kämpften einen Krieg ohne Ende. Ich bin erstaunt, dass unsere Ausrüstung noch in einem so guten Zustand ist.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10275;
UPDATE `locales_npc_text` SET `ID` = 10276,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gerade in diesem Moment kämpfen Untote wie Ihr gegen den Bund von Arathor im Arathibecken. Werdet Ihr den Entweihten dabei helfen, die Menschen und ihre Verbündeten der Allianz zu besiegen?' WHERE `locales_npc_text`.`ID` = 10276;
UPDATE `locales_npc_text` SET `ID` = 10277,`Text0_0_loc3` = 'Throm''ka, $N. Die Kriegshymnenschlucht braucht Helden wie Euch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10277;
UPDATE `locales_npc_text` SET `ID` = 10279,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10279;
UPDATE `locales_npc_text` SET `ID` = 10280,`Text0_0_loc3` = 'Grüße, Reisender. Ich bin Oronok. Bitte, setzt Euch an mein Feuer und wärmt Euch auf.$B$BGebt auf die gefräßigen Biester in der Untiefe vor uns Acht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10280;
UPDATE `locales_npc_text` SET `ID` = 10282,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Vergesst nicht, $C, die Schlachten in Azeroth toben immer noch. Werdet Ihr zurückkehren und der Horde im Alteractal helfen?' WHERE `locales_npc_text`.`ID` = 10282;
UPDATE `locales_npc_text` SET `ID` = 10283,`Text0_0_loc3` = 'Throm''ka, $C. Wusstet Ihr, dass unser Kampf gegen die Schildwachen der Silberschwingen in der Kriegshymnenschlucht immer noch andauert? Eure Anwesenheit wäre dort sehr willkommen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10283;
UPDATE `locales_npc_text` SET `ID` = 10284,`Text0_0_loc3` = 'Ich bin weit gereist um einen Weg zu finden, wie ich den Bund von Arathor im Arathibecken endgültig besiegen kann, aber Adam Eternum, dieser Trottel im Lendenschurz, verfolgt mich auf Schritt und Tritt.$B$BWenn jedoch Kämpfer wie Ihr nach Azeroth zurückkehren würden, könnten wir die Allianz vielleicht doch ein für allemal besiegen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10284;
UPDATE `locales_npc_text` SET `ID` = 10285,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wie geht''s? Schon genug von der Scherbenwelt? Bereit zur Rückkehr ins Alteractal und der Horde eine zu verpassen?' WHERE `locales_npc_text`.`ID` = 10285;
UPDATE `locales_npc_text` SET `ID` = 10286,`Text0_0_loc3` = 'Die Schildwachen der Silberschwingen kämpfen immer noch in der Kriegshymnenschlucht gegen die Horde, $C. Wollt Ihr dorthin zurückkehren, um ihnen zu helfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10286;
UPDATE `locales_npc_text` SET `ID` = 10287,`Text0_0_loc3` = 'Ich kam in die Scherbenwelt in der Hoffnung, ein mächtiges Artefakt zu finden, um den Bund von Arathor im Kampf gegen die Entweihten im Arathibecken zu unterstützen. Alles, was ich bisher gefunden habe, ist dieser schicke magische Lendenschurz, dieser lächerliche Gnom neben mir und ein seltsamer Untoter, der behauptet, ich hätte seinen grauen Schädel gestohlen oder so.$B$BEgal, wärt Ihr bereit, Euch dem Bund im Kampf anzuschließen, während ich meine Suche fortsetze?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10287;
UPDATE `locales_npc_text` SET `ID` = 10288,`Text0_0_loc3` = '<Man bekommt den Eindruck, dass Oric hier nur als Pausenclown herumsteht und nicht beachtet werden sollte. Das würde ihn nur ermutigen.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10288;
UPDATE `locales_npc_text` SET `ID` = 10290,`Text0_0_loc3` = '<Es ist eine riesige, gepanzerte Kampfkatze mit Sattel. Ist das nicht schon lächerlich genug? Soll sie auch noch reden können?>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10290;
UPDATE `locales_npc_text` SET `ID` = 10291,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Nur ein Magier kann die Portalmagie erlernen, $C.' WHERE `locales_npc_text`.`ID` = 10291;
UPDATE `locales_npc_text` SET `ID` = 10292,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ein Magier... Ihr wollt also die Magie der Portale erlernen, ja?' WHERE `locales_npc_text`.`ID` = 10292;
UPDATE `locales_npc_text` SET `ID` = 10293,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die königliche Apothekervereinigung aus Unterstadt ist begierig darauf, unseren Orcverbündeten hier in der Scherbenwelt zu helfen. Es ist eine Möglichkeit, unsere Verbundenheit zu stärken, und neue Tier- und Pflanzenarten sowie neue Gemische zu studieren.$B$BGemische... wie zum Beispiel Gifte.' WHERE `locales_npc_text`.`ID` = 10293;
UPDATE `locales_npc_text` SET `ID` = 10299,`Text0_0_loc3` = 'Der Weißgipfelposten ist ein ungeplanter Halt für mich, um es milde auszudrücken. Meine Kunden zählen darauf, dass ich meine Waren ausliefere, und ich beginne daran zu zweifeln, ob ich es bei all diesen Verzögerungen rechtzeitig schaffe.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10299;
UPDATE `locales_npc_text` SET `ID` = 10300,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Heh, nicht den Käfig öffnen! Verstanden?$B$BDie Oger haben mich genau da, wo ich will.' WHERE `locales_npc_text`.`ID` = 10300;
UPDATE `locales_npc_text` SET `ID` = 10301,`Text0_0_loc3` = 'Ich habe gehört, der Staub und die Hitze der Höllenfeuerhalbinsel sollen sehr lästig sein... Ich kann das nicht beurteilen, aber ich vertraue meinen geehrten, noch lebenden Verbündeten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10301;
UPDATE `locales_npc_text` SET `ID` = 10302,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10302;
UPDATE `locales_npc_text` SET `ID` = 10305,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr kauft hier keinen Falkenschreiter, bevor Ihr Euch unserem Volk gegenüber nicht als würdig und ehrerbietig bewiesen habt.$B$B$BKehrt erst zurück, wenn die Blutelfen Euch mit Ehrfurcht begegnen. Fort mit Euch!' WHERE `locales_npc_text`.`ID` = 10305;
UPDATE `locales_npc_text` SET `ID` = 10306,`Text0_0_loc3` = 'Diese Orcs scheinen es sehr eilig zu haben, die Scherbenwelt für sich zu beanspruchen! Wenn ich Euch sagen würde, wie viel sie mir für meine Dienste bezahlen, würdet Ihr mich vor Neid töten!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10306;
UPDATE `locales_npc_text` SET `ID` = 10308,`Text0_0_loc3` = 'Wenn Ihr das nötige Gold habt, kann ich Euch beibringen, wie man auf einem Falkenschreiter reitet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10308;
UPDATE `locales_npc_text` SET `ID` = 10310,`Text0_0_loc3` = 'Die Blutelfen müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10310;
UPDATE `locales_npc_text` SET `ID` = 10312,`Text0_0_loc3` = 'Seid Ihr bereit, um die Geschichte des Worts der Verdammnis zu hören, $N?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10312;
UPDATE `locales_npc_text` SET `ID` = 10313,`Text0_0_loc3` = 'Gut. Die Worte, die Ihr sucht, haben einen Namen, $N. Sie sind als Litanei der Verdammnis bekannt. Es sind die Worte, mit denen Gul''dan selbst dieses Land zerschlagen hat; der Spruch, der das letzte Band, was noch zwischen den Orcs und den Elementen von Draenor herrschte, zerrissen hat.$B$B<Oronok nickt.>$B$BJa, Ihr müsst diese Litanei wiederherstellen und die Worte lesen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10313;
UPDATE `locales_npc_text` SET `ID` = 10314,`Text0_0_loc3` = 'Die Litanei der Verdammnis wurde zwanzig Jahre lang in Gul''dans Privatgemächern in Karabor aufbewahrt. Ah, richtig, Ihr habt keine Ahnung, was Karabor ist, richtig?$B$BDer Schwarze Tempel...$B$BNun ist er das Zuhause von Illidan Sturmgrimm.$B$BAls Illidan die Kontrolle über den Schwarzen Tempel gewonnen hat, hat er das Wort entdeckt. Er erkannte, dass der Schaden dieser Welt bereits zugefügt wurde, aber er bewahrte die Litanei dennoch für spätere Zwecke auf.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10314;
UPDATE `locales_npc_text` SET `ID` = 10315,`Text0_0_loc3` = 'Wenn Ihr vor dem Altar der Verdammnis steht und die Geister der Verdammten seht, die im Gebet versunken sind, dann seht Ihr nicht, wie ich neben Gul''dan stehe.$B$B<Oronok nickt.>$B$BIch habe doch gesagt, dass ich schon vieles war, oder? Darunter auch ein Leutnant Gul''dans.$B$BIch habe in meinem Leben schon viele schreckliche Dinge getan. Ich habe Männer, Frauen und Kinder abgeschlachtet. Ich habe im Blut unschuldiger Draenei getanzt... und doch...$B$B<Oronok verschluckt sich an seinen eigenen Worten.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10315;
UPDATE `locales_npc_text` SET `ID` = 10316,`Text0_0_loc3` = 'Diesen Tag werde ich nie vergessen, $R. Nie. Auch wenn ich vor langer Zeit dem Schamanismus zugunsten der Schattenmagie den Rücken gekehrt hatte, fühlte ich den Schnitt. So ein tiefes Gefühl von Verlust... Ich konnte es nicht in Worte fassen.$B$BAls das Band zu den Elementen zerrissen war, haben sich alle Orcs in Draenor verändert. Wenn es je einen Zweifel an der Macht der Schatten gegeben hatte, so wurde er auf einmal mit der geschmolzenen Lava von dieser zerschlagenen Erde weggewischt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10316;
UPDATE `locales_npc_text` SET `ID` = 10317,`Text0_0_loc3` = 'Eine Welle der Übelkeit überkam mich. Nachdem das Ritual vorbei war, rannte ich nach Hause zu meinen Kindern. Ich habe alle Habseligkeiten, die ich mit bloßen Händen tragen konnte, geschnappt und bin mit meinen Söhnen geflüchtet. Wir rannten in die Berge und haben uns versteckt. Wir hatten keine andere Wahl...$B$BEs vergeht nicht ein Tag, an dem ich nicht an den Schnitt denke. Es hat zwanzig Jahre gedauert, wieder eine Verbindung zu den Elementen herzustellen. Und es hat zwanzig Jahre gedauert, bis die Elemente begonnen haben zu heilen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10317;
UPDATE `locales_npc_text` SET `ID` = 10318,`Text0_0_loc3` = 'Wie ich bereits gesagt habe, wurde die Litanei von Illidan gefunden, als er den Schwarzen Tempel eingenommen hatte. Er hat den Folianten in drei Teile geteilt und sie seinen Dienern anvertraut.$B$BMeine Jungs... Meine Jungs sind nun erwachsen. Als ich erfuhr, was Illidan mit dem Wort getan hatte, habe ich meine Söhne losgeschickt, um sie wiederzuholen.$B$BIhr müsst meine Jungs finden, $N. Ihr müsst sie finden und die Litanei der Verdammnis zurückholen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10318;
UPDATE `locales_npc_text` SET `ID` = 10321,`Text0_0_loc3` = 'Willkommen in Shattrath, dem Sitz der Sha''tar in der Scherbenwelt. Soll ich Euch den Weg weisen?',`Text0_1_loc3` = 'Willkommen in Shattrath, dem Sitz der Sha''tar in der Scherbenwelt. Soll ich Euch den Weg weisen?' WHERE `locales_npc_text`.`ID` = 10321;
UPDATE `locales_npc_text` SET `ID` = 10322,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kam hier her, um die primitive Kultur des früheren Klans der Donnerfürsten zu studieren.$B$BSie waren schwach und erlagen der Verlockung der Macht, die sie durch die Verwandlung in Höllenorcs durch Mannoroths Blut erhielten.$B$BIch habe alle ihre Texte gelesen. Besonders interessiert bin ich an der Bergung eines Artefakts, einer Trommel, eines Pfeils und einer Tafel, die verloren gingen, als wir die Oger der Speerspießer schlugen und zurück in ihre Festung drängten.' WHERE `locales_npc_text`.`ID` = 10322;
UPDATE `locales_npc_text` SET `ID` = 10323,`Text0_0_loc3` = 'Ar''tors lebloser Körper wird weiterhin von dämonischer Magie in der Luft gehalten. Selbst im Tode wird er noch gemartert.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10323;
UPDATE `locales_npc_text` SET `ID` = 10324,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich freue mich, dass Ihr Euch entschieden habt, Euch unserer Sache anzuschließen. Als Belohnung werde ich einen Ring mit den Resten aus dem Brunnen der Ewigkeit, die Ihr mir gebracht habt, verstärken.$B$BIch biete Euch vier verschiedene Pfade an, über die Ihr seine Macht entfesseln könnt. Wählt weise, $N. Die Sande der Zeit fließen nie nur ein eine Richtung.' WHERE `locales_npc_text`.`ID` = 10324;
UPDATE `locales_npc_text` SET `ID` = 10327,`Text0_0_loc3` = 'Ich... bin gestorben.$B$BWarum bin ich immer noch hier? Gibt es keine ewigen Jagdgründe, in die ich aufsteige?$B$BNein... zuerst muss ich meine Aufgabe erledigen. Die Litanei der Verdammnis muss gefunden werden, bevor ich diese Welt verlassen kann.',`Text0_1_loc3` = 'Ich... bin gestorben.$B$BWarum bin ich immer noch hier? Gibt es keine ewigen Jagdgründe, in die ich aufsteige?$B$BNein... zuerst muss ich meine Aufgabe erledigen. Die Litanei der Verdammnis muss gefunden werden, bevor ich diese Welt verlassen kann.' WHERE `locales_npc_text`.`ID` = 10327;
UPDATE `locales_npc_text` SET `ID` = 10329,`Text0_0_loc3` = 'Eure Ausbildung als Schwertschmied zu verlernen ist keine Entscheidung, die man leichtfertig treffen sollte. Wenn Ihr Euch entschließt, sie aufzugeben, werdet Ihr alle Rezepte, die die Schwertschmiedekunst benötigen, verlieren!',`Text0_1_loc3` = 'Eure Ausbildung als Schwertschmied zu verlernen ist keine Entscheidung, die man leichtfertig treffen sollte. Wenn Ihr Euch entschließt, sie aufzugeben, werdet Ihr alle Rezepte, die die Schwertschmiedekunst benötigen, verlieren!' WHERE `locales_npc_text`.`ID` = 10329;
