UPDATE `locales_npc_text` SET `ID` = 10339,`Text0_0_loc3` = 'Ich habe meine Zeit in den Schützengräben verbracht und viele Jahre in diversen Gasthäusern trainiert. Es ist schön, wieder zuhause zu sein. Egal, was daraus geworden ist, es ist mein Zuhause.',`Text0_1_loc3` = 'Ich habe meine Zeit in den Schützengräben verbracht und viele Jahre in diversen Gasthäusern trainiert. Es ist schön, wieder zuhause zu sein. Egal, was daraus geworden ist, es ist mein Zuhause.' WHERE `locales_npc_text`.`ID` = 10339;
UPDATE `locales_npc_text` SET `ID` = 10341,`Text0_0_loc3` = 'Die Oger! Sie haben mich hier bis zum Hals eingegraben und vergessen, zurückzukommen!$B$BJa, Mann, lustig! Lach nur. Körperloser Geisterkopf hier.$B$BWenn du mir natürlich helfen willst, werde ich dich entsprechend belohnen.$B$BNee nee, lach ruhig weiter.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10341;
UPDATE `locales_npc_text` SET `ID` = 10349,`Text0_0_loc3` = '<Nuainn Sturmbart schüttelt den Kopf.>$B$BWir müssen alles aufbringen, was wir nur können, um die Feste gegen die Legion auf der einen und Illidans Dienern auf der anderen Seite zu verteidigen. aber wir werden es schaffen! Jeder echte Wildhammer würde lieber sterben, als sein Zuhause aufzugeben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10349;
