UPDATE `locales_npc_text` SET `ID` = 10458,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich wünschte, ich hätte genug Zeit, um alle zu lehren, die zu mir kommen. Leider ist das nicht der Fall. Solange Ihr nicht alles gelernt habt, was Padaar Euch beibringt, kann ich Euch nicht helfen.' WHERE `locales_npc_text`.`ID` = 10458;
