UPDATE `locales_npc_text` SET `ID` = 10466,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr bei Razaks Raunacken mitmacht, lernt Ihr ein, zwei Dinge über das Befolgen von Befehlen.$B$BErstens: Ihr kämpft bis zum Tod. Zweitens: Siehe erstens.' WHERE `locales_npc_text`.`ID` = 10466;
