UPDATE `locales_npc_text` SET `ID` = 10554,`Text0_0_loc3` = 'Diese Druiden haben eine enorme Kraftquelle erschlossen! Sie können sich in Tiere verwandeln, Blitze und starkes Mondlicht erzeugen... Ich muss herausfinden, wie sie das machen!$B$BVielleicht tragen sie Batterien unter diesen Roben...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10554;
