UPDATE `locales_npc_text` SET `ID` = 10556,`Text0_0_loc3` = '<Der Arakkoa wirft Euch einen gepeinigten Blick zu und starrt dann abwechselnd auf Euch und durch Euch hindurch.>$B$BWas ist das Ergebnis unserer Mühen? Eine größere Monstrosität als die, welche wir verhindern wollten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10556;
