UPDATE `locales_npc_text` SET `ID` = 10559,`Text0_0_loc3` = 'Die Manazellen im Auge des Sturms sind wertvoll. Wir können es uns nicht leisten, sie an die Draenei zu verlieren! Ihr dort! Macht Euch nützlich und unterstützt uns im Kampf!',`Text0_1_loc3` = 'Die Manazellen im Auge des Sturms sind wertvoll. Wir können es uns nicht leisten, sie an die Draenei zu verlieren! Ihr dort! Macht Euch nützlich und unterstützt uns im Kampf!' WHERE `locales_npc_text`.`ID` = 10559;
UPDATE `locales_npc_text` SET `ID` = 10560,`Text0_0_loc3` = '$N, kommt her! Ihr habt sicherlich schon von der Schlacht gehört, die im Alteractal stattfindet. Wir brauchen alle fähigen Kämpfer, um mit der Sturmlanzengarde gegen die Frostwölfe zu kämpfen!',`Text0_1_loc3` = '$N, kommt her! Ihr habt sicherlich schon von der Schlacht gehört, die im Alteractal stattfindet. Wir brauchen alle fähigen Kämpfer, um mit der Sturmlanzengarde gegen die Frostwölfe zu kämpfen!' WHERE `locales_npc_text`.`ID` = 10560;
UPDATE `locales_npc_text` SET `ID` = 10561,`Text0_0_loc3` = 'Dies ist ein sehr langer Flug zum singenden Bergrücken. Sehr gefährlich!$B$BDafür benötige ich einen unterzeichneten Haftungsausschluss...',`Text0_1_loc3` = 'Dies ist ein sehr langer Flug zum singenden Bergrücken. Sehr gefährlich!$B$BDafür benötige ich einen unterzeichneten Haftungsausschluss...' WHERE `locales_npc_text`.`ID` = 10561;
UPDATE `locales_npc_text` SET `ID` = 10562,`Text0_0_loc3` = 'Wir freuen uns immer über weitere Flugdaten! Wohin wollt Ihr?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10562;
