UPDATE `locales_npc_text` SET `ID` = 10565,`Text0_0_loc3` = 'Obwohl die Erforschung der neuen Regionen zweifellos wichtig ist, dürft Ihr Azeroth nicht vergessen! Wir brauchen Euch mehr denn je im Kampf gegen die Entweihten. Die Ressourcen müssen gesichert werden!',`Text0_1_loc3` = 'Obwohl die Erforschung der neuen Regionen zweifellos wichtig ist, dürft Ihr Azeroth nicht vergessen! Wir brauchen Euch mehr denn je im Kampf gegen die Entweihten. Die Ressourcen müssen gesichert werden!' WHERE `locales_npc_text`.`ID` = 10565;
UPDATE `locales_npc_text` SET `ID` = 10566,`Text0_0_loc3` = 'Ihr da, $C! Wir brauchen Eure Hilfe! Die hässlichen Orcs des Kriegshymnenklans zerstören unser schönes Eschental, und wir brauchen Eure Kampfkraft, um sich ihnen auf dem Schlachtfeld zu stellen. Kehrt mit uns nach Azeroth zurück und kämpft für die Ehre der Silberschwingen! Werdet Ihr Euch uns anschließen?',`Text0_1_loc3` = 'Ihr da, $C! Wir brauchen Eure Hilfe! Die hässlichen Orcs des Kriegshymnenklans zerstören unser schönes Eschental, und wir brauchen Eure Kampfkraft, um sich ihnen auf dem Schlachtfeld zu stellen. Kehrt mit uns nach Azeroth zurück und kämpft für die Ehre der Silberschwingen! Werdet Ihr Euch uns anschließen?' WHERE `locales_npc_text`.`ID` = 10566;
UPDATE `locales_npc_text` SET `ID` = 10568,`Text0_0_loc3` = 'Es gibt ein altes Sprichwort: "Gebt einem Mann einen Fisch, und er ist satt für einen Tag. Lehrt einen Mann zu fischen, und er ist sein Leben lang satt." Am besten schmecken die Fische natürlich gekocht, und das ist meine Spezialität.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10568;
UPDATE `locales_npc_text` SET `ID` = 10569,`Text0_0_loc3` = 'Hallo, $N! Was machen Eure Abenteuer? Ich komme mit meinen Experimenten gut voran...$B$BBitte kommt später wieder vorbei! Ich könnte mehr Testflüge für Euch haben!',`Text0_1_loc3` = 'Hallo, $N! Was machen Eure Abenteuer? Ich komme mit meinen Experimenten gut voran...$B$BBitte kommt später wieder vorbei! Ich könnte mehr Testflüge für Euch haben!' WHERE `locales_npc_text`.`ID` = 10569;
UPDATE `locales_npc_text` SET `ID` = 10570,`Text0_0_loc3` = 'Kraft und Ehre, $C. Wir brauchen mutige Kämpfer wie Euch im Alteractal. Das Gebiet der Frostwölfe muss verteidigt werden! Werdet Ihr nach Azeroth zurückkehren und uns Hilfe leisten?',`Text0_1_loc3` = 'Kraft und Ehre, $C. Wir brauchen mutige Kämpfer wie Euch im Alteractal. Das Gebiet der Frostwölfe muss verteidigt werden! Werdet Ihr nach Azeroth zurückkehren und uns Hilfe leisten?' WHERE `locales_npc_text`.`ID` = 10570;
UPDATE `locales_npc_text` SET `ID` = 10571,`Text0_0_loc3` = '$R! Ja, Ihr! Warum verschwendet Ihr Eure Zeit in dieser Ruinenstadt, wenn Euer angestammter Platz im Arathibecken ist? Der Bund von Arathor greift gnadenlos an. Wir brauchen Eure Hilfe, und bieten reiche Belohnung!',`Text0_1_loc3` = '$R! Ja, Ihr! Warum verschwendet Ihr Eure Zeit in dieser Ruinenstadt, wenn Euer angestammter Platz im Arathibecken ist? Der Bund von Arathor greift gnadenlos an. Wir brauchen Eure Hilfe, und bieten reiche Belohnung!' WHERE `locales_npc_text`.`ID` = 10571;
UPDATE `locales_npc_text` SET `ID` = 10572,`Text0_0_loc3` = '$C, daheim auf Azeroth hindern die Schildwachen der Silberschwingen unser Vorankommen in den Wäldern. Wir brauchen Euch an der Front, um sie zurückzudrängen! Ihr werdet große Ehre und hohen Ruf erlangen, wenn Ihr Euch der Vorhut des Kriegshymnenklans anschließt!',`Text0_1_loc3` = '$C, daheim auf Azeroth hindern die Schildwachen der Silberschwingen unser Vorankommen in den Wäldern. Wir brauchen Euch an der Front, um sie zurückzudrängen! Ihr werdet große Ehre und hohen Ruf erlangen, wenn Ihr Euch der Vorhut des Kriegshymnenklans anschließt!' WHERE `locales_npc_text`.`ID` = 10572;
UPDATE `locales_npc_text` SET `ID` = 10573,`Text0_0_loc3` = 'Rettet Euch selbst, $R. Flüchtet von diesem Ort! Das Purpursiegel wird Euch hinrichten, wenn sie Euch entdeckten.',`Text0_1_loc3` = 'Rettet Euch selbst, $R. Flüchtet von diesem Ort! Das Purpursiegel wird Euch hinrichten, wenn sie Euch entdeckten.' WHERE `locales_npc_text`.`ID` = 10573;
