UPDATE `locales_npc_text` SET `ID` = 10601,`Text0_0_loc3` = 'Heh! Habt Ihr vom Kampfwettbewerb des Dampfdruckkartells gehört?$B$Bhr könnt Teamsatzungen bei den Arenaveranstaltern in Gadgetzan und außerhalb der Arenen in Nagrand und im Schergrat erwerben.',`Text0_1_loc3` = 'Heh! Habt Ihr vom Kampfwettbewerb des Dampfdruckkartells gehört?$B$Bhr könnt Teamsatzungen bei den Arenaveranstaltern in Gadgetzan und außerhalb der Arenen in Nagrand und im Schergrat erwerben.' WHERE `locales_npc_text`.`ID` = 10601;
