UPDATE `locales_npc_text` SET `ID` = 10705,`Text0_0_loc3` = 'Ich werde Euch nicht ausbilden, solange Ihr bei den Draenei keinen ehrfürchtigen Ruf erlangt habt.',`Text0_1_loc3` = 'Ich werde Euch nicht ausbilden, solange Ihr bei den Draenei keinen ehrfürchtigen Ruf erlangt habt.' WHERE `locales_npc_text`.`ID` = 10705;
UPDATE `locales_npc_text` SET `ID` = 10706,`Text0_0_loc3` = 'Habt vielen Dank! Der Schleier der Finsternis, welcher auf meiner Seele lag, ist verschwunden! Ich kann wieder klar sehen... und erblicke das Antlitz meines Retters!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10706;
UPDATE `locales_npc_text` SET `ID` = 10707,`Text0_0_loc3` = 'Verschwindet, $C! Lasst uns gewähren, oder Ihr seid als nächstes dran und dann werdet Ihr wahre Verzweiflung erfahren!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10707;
UPDATE `locales_npc_text` SET `ID` = 10717,`Text0_0_loc3` = 'Macht Euch keine Sorgen. Der Trümmerposten ist robust und wird schon halten! Aber nur für den Fall dass er doch mal auseinanderfallen sollte, haltet Euch immer bereit, auf einen Greifen zu springen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10717;
UPDATE `locales_npc_text` SET `ID` = 10718,`Text0_0_loc3` = 'Nun, da Ihr mich besiegt habt, erprobt Eure Fertigkeiten gegeneinander! Übernehmt die Kontrolle über einen der Könige, um mit dem Spiel zu beginnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10718;
UPDATE `locales_npc_text` SET `ID` = 10719,`Text0_0_loc3` = '<Die Stimme von A''dal erklingt beruhigend in Euren Gedanken.>$B$BSorgt Euch nicht, Kind. Schaut in Eure Tasche, und Ihr werdet ihn wieder finden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10719;
UPDATE `locales_npc_text` SET `ID` = 10720,`Text0_0_loc3` = 'Ihr wünscht, mich zu sprechen, $R?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10720;
UPDATE `locales_npc_text` SET `ID` = 10721,`Text0_0_loc3` = 'Ich habe in diesem Ödland viele Gefahren gesehen. Wer weiß schon, welche Kreaturen im Staub lauern, um die Ahnungslosen anzufallen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10721;
