UPDATE `locales_npc_text` SET `ID` = 10734,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Warum heißt die Scherbenwelt denn nicht mehr Draenor?' WHERE `locales_npc_text`.`ID` = 10734;
UPDATE `locales_npc_text` SET `ID` = 10735,`Text0_0_loc3` = '<Morthis blickt zum Himmel.>$B$BAuf eine gewisse Weise beneide ich die Freiheit, die Krähe und Corvax genießen. Seine irdischen Verpflichtungen abzustreifen ist in der Tat ein großer Segen.$B$BDie Zukunft wird mir erneut Flügel verleihen, aber zuerst muss ich eine Aufgabe erledigen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10735;
UPDATE `locales_npc_text` SET `ID` = 10736,`Text0_0_loc3` = 'Es war wirklich sehr nett, dass Gezhe mich hierher kommen ließ, um zu studieren und all diese interessanten Sachen zu lernen, $G werter Herr : werte Dame;.$B$B<Jheel lässt seinen Kopf hängen.>$B$BIch hasse es jedoch, nicht bei meiner Schwester sein zu können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10736;
UPDATE `locales_npc_text` SET `ID` = 10741,`Text0_0_loc3` = 'Aran wurde bezwungen? Ja... ich kann spüren, dass sich sein Griff gelöst hat - zumindest ein klein bisschen. Es ist gerade genug, damit ich meine Aufgabe als Pförtner erfüllen kann! Möchtet Ihr, dass ich Euch zur Bibliothek des Wächters bringe, $N?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10741;
