UPDATE `locales_npc_text` SET `ID` = 10975,`Text0_0_loc3` = 'Wir haben ein wahres Festmahl für Euch, Freund.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10975;
UPDATE `locales_npc_text` SET `ID` = 10976,`Text0_0_loc3` = 'Welch schmackhafte Leckerei dürfen wir Euch anbieten?',`Text0_1_loc3` = 'Welch schmackhafte Leckerei dürfen wir Euch anbieten?' WHERE `locales_npc_text`.`ID` = 10976;
UPDATE `locales_npc_text` SET `ID` = 10977,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'He, Kamerad. Wenn Ihr Euch in der Himmelswache bewiesen habt, kann ich Euch einen direkten Flug zum Schattenwindtal in den Skethylbergen anbieten.$B$BGefällt Euch mein schöner Netherrochen? Er ist wundervoll.' WHERE `locales_npc_text`.`ID` = 10977;
UPDATE `locales_npc_text` SET `ID` = 10978,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hallo $N. Müsst Ihr schnell zum Schattenwindlager, unserem Posten in den Skethylbergen? Ich habe einen schönen kleinen Netherrochen, der nur darauf wartet.$B$BWollt Ihr dorthin? Ihr müsst es nur sagen, $G großer Junge : Häschen;!' WHERE `locales_npc_text`.`ID` = 10978;
UPDATE `locales_npc_text` SET `ID` = 10979,`Text0_0_loc3` = 'Hallo, $G Sire : holde Dame;! Wenn Ihr es wünscht, kann Euch mein Netherrochen hier zum Außenposten der Himmelswache oben im Schergrat bringen.$B$BNur, damit Ihr Bescheid wisst.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10979;
UPDATE `locales_npc_text` SET `ID` = 10980,`Text0_0_loc3` = 'Einen guten Tag wünsche ich Euch. Wenn Ihr bei der Himmelswache der Sha''tari etwas weiter im Rang aufgestiegen seid, werde ich Euch einen direkten Flug zum Außenposten der Himmelswache, unserem Stützpunkt oben im Schergrat, anbieten können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10980;
