UPDATE `locales_npc_text` SET `ID` = 10988,`Text0_0_loc3` = 'Habt Ihr nichts besseres zu tun, als mich vollzulabern?$B$BNun zieht los und zeigt, was Ihr wert seid!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10988;
UPDATE `locales_npc_text` SET `ID` = 10989,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10989;
UPDATE `locales_npc_text` SET `ID` = 10990,`Text0_0_loc3` = 'Ihr werdet langsam zu einer sehr respektierten Person hier, $GJunge:Mädel;! Wenn ich es nicht besser wüsste, würde ich sagen, Ihr seid scharf auf meinen Job!$B$BEgal, wenn Ihr so weiter macht, hab Ihr es Euch verdient!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10990;
UPDATE `locales_npc_text` SET `ID` = 10991,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10991;
UPDATE `locales_npc_text` SET `ID` = 10992,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich merke, dass Ihr einiges an Zeit für uns opfert, $C.$B$BGebt noch mehr, die Dämonen sind noch nicht weg, Neuling.' WHERE `locales_npc_text`.`ID` = 10992;
UPDATE `locales_npc_text` SET `ID` = 10993,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10993;
UPDATE `locales_npc_text` SET `ID` = 10994,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Euren Namen hört man überall im Außenposten. Lasst Euch das nicht zu Kopf steigen, ich weiss, dass Ihr noch mehr drauf habt!$B$BIch behalte Euch im Auge, Neuling.' WHERE `locales_npc_text`.`ID` = 10994;
UPDATE `locales_npc_text` SET `ID` = 10995,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Also, ich gebe zu, Ihr habt Euch bewiesen, und mehr, $N.$B$BWenn Ich vorher hart zu Euch war, lag das daran, dass ich ein Fünkchen einer wahren Himmelswache in Euch gesehen habe. Wenn ich das so sagen darf, Ihr seid unser bestes Fliegerass!$B$BWann immer Ihr aushelfen wollt, gebe ich Euch gern einen weiteren Satz Bomben.' WHERE `locales_npc_text`.`ID` = 10995;
UPDATE `locales_npc_text` SET `ID` = 10996,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10996;
UPDATE `locales_npc_text` SET `ID` = 10997,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'He da, $C!$B$B<Khatie schaut Euch gewitzt an.>$B$BWenn Ihr uns noch mehr Rochen bändigen wollt, dann kommt einfach bei mir vorbei, ja?' WHERE `locales_npc_text`.`ID` = 10997;
UPDATE `locales_npc_text` SET `ID` = 10998,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10998;
UPDATE `locales_npc_text` SET `ID` = 10999,`Text0_0_loc3` = 'Freund! Zu lange ist es schon her. Was können wir für Euch tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 10999;
UPDATE `locales_npc_text` SET `ID` = 11000,`Text0_0_loc3` = 'Wonach sucht Ihr?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11000;
UPDATE `locales_npc_text` SET `ID` = 11001,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wisst Ihr, $N, es ist eine große Ehre, mit Euch zusammen zu arbeiten!$B$BWenn Ihr gerade nicht mit Bändigen beschäftigt seid, könnten wir ja vielleicht $G mal irgendwo etwas essen gehen? : mal einen Frauenabend machen?;$B$BOh, habe ich Euch schon erzählt, dass man jetzt auch ganz schnell zu unserem Lager in den Skethylbergen fliegen kann? Wenn Ihr das möchtet, dann sprecht mit Tierführerin Irena darüber!' WHERE `locales_npc_text`.`ID` = 11001;
UPDATE `locales_npc_text` SET `ID` = 11002,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'So, jetzt seid Ihr ja schon eine Weile hier, aber wir scheinen uns immer zu verpassen, um nach der Arbeit was zusammen zu unternehmen.$B$BLiegt es an mir?$B$B<Khatie zwingt sich, heiter auszusehen.>$B$BEgal, ich wollte nur sagen, dass ich... wir wirklich dankbar für alles sind, was Ihr für die Himmelswache tut!' WHERE `locales_npc_text`.`ID` = 11002;
UPDATE `locales_npc_text` SET `ID` = 11004,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich hoffe, dass Ihr nicht denkt, dass ich Euch verfolge, $N.$B$BIch meine, ich weiß, dass Ihr jetzt in der Himmelswache sehr berühmt seid und so... und ich bin mir sicher, dass Ihr Euch nicht mit einem niedrigen Peon wie mir abgeben würdet.$B$BAber wenn Ihr Euch je treffen oder einfach nur so rumhängen wollt oder ein noch ein paar Rochen bändigen wollt, dann kommt jederzeit vorbei! Ich werde hier sein.$B$BIch vermisse Euch...' WHERE `locales_npc_text`.`ID` = 11004;
UPDATE `locales_npc_text` SET `ID` = 11007,`Text0_0_loc3` = 'Die Relikte sind ein wahrhaft großes Mysterium. Wenn Ihr sie regelmäßig studiert, könnt Ihr ihre Fähigkeiten vielleicht besser einschätzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11007;
