UPDATE `locales_npc_text` SET `ID` = 11009,`Text0_0_loc3` = '<Chu''a''lors linker Kopf sagt:>$B$BWir finden den Eifer, mit dem Ihr uns helft, ansprechend...$B$B<Sein rechter Kopf setzt fort:>$B$B...und wir möchten Euch wissen lassen, dass wir Eure Anstrengungen würdigen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11009;
UPDATE `locales_npc_text` SET `ID` = 11010,`Text0_0_loc3` = 'Die Leute hier beginnen, Euren Namen mit Respekt auszusprechen.$B$B<Chu''a''lors linkes Auge durchbohrt Euch mit einem steinigen Blick.>$B$BMacht weiter so, $N!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11010;
UPDATE `locales_npc_text` SET `ID` = 11011,`Text0_0_loc3` = 'Zuerst wurdet Ihr $GKönig:Königin; der Oger dort unten, nun habt Ihr ehrfürchtigen Ruf bei uns von Ogri''la.$B$B$GKein:Keine:r; $R hat das jemals geschafft, doch Ihr seid der lebende Beweis. Ich danke Euch, $N, wir alle stehen tief in Eurer Schuld. Wir werden Euch niemals ausreichend dafür danken können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11011;
