UPDATE `locales_npc_text` SET `ID` = 11016,`Text0_0_loc3` = 'Hier in Ogri''la haben wir genug Zeit, um das Rätsel des Stahls zu lösen. Aber genug davon, wie können wir Euch helfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11016;
