UPDATE `locales_npc_text` SET `ID` = 11020,`Text0_0_loc3` = 'Gahk mögen kleines $R $N. Ihr töten großen Dämon, jetzt alle kleinen Dämons Angst haben!$B$B<Beide Köpfe von Gahk grinsen Euch an.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11020;
UPDATE `locales_npc_text` SET `ID` = 11021,`Text0_0_loc3` = 'Mir sicher Euch sehen hier ganze Zeit!$B$BChu''a''lor sagen kleines $R unser $GHeld:Heldin;!$B$B$N jetzt wie einer von uns Ogern. Nur, Gahk verwirrt sein weil $N muss größer seind dafür!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11021;
