UPDATE `locales_npc_text` SET `ID` = 11035,`Text0_0_loc3` = 'Denkt daran, $N, der Krempel steht Euch zur Verfügung, wenn Ihr gute Taten begeht. Ist sowas wie Karma oder so.$B$BAlso, helft wo Ihr könnt, auch bei unseren Freunden von der Himmelswache im Norden.$B$BDas wäre toll... danke!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11035;
UPDATE `locales_npc_text` SET `ID` = 11036,`Text0_0_loc3` = 'Chu''a''lor sagt nette Sachen über Euch, $N. Wenn Ihr wollt, haben wir ein paar Sorten spezielles Ogergebräu für Euch zum Verkauf.$B$BDie sind echt lecker!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11036;
UPDATE `locales_npc_text` SET `ID` = 11037,`Text0_0_loc3` = 'Die Leute hier begegnen Euch mit Respekt, $GMann:Häschen;.Es scheint, als ob jeder über Euch redet. Das ist super.$B$BLasst mich sehen, ob ich etwas für Euch auf Lager habe. Wir haben viele nette Sachen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11037;
