UPDATE `locales_npc_text` SET `ID` = 11056,`Text0_0_loc3` = 'Wir beginnen einen Angriff auf den Landeplatz von Bash''ir, um die Kristallschmiede zu studieren. Helft uns, wenn Ihr könnt! Unser Äthertechniker der Himmelswache startet in $4581d.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11056;
UPDATE `locales_npc_text` SET `ID` = 11057,`Text0_0_loc3` = 'Der Schädelhaufen stinkt nach Fäulnis. Ihr befürchtet, dass die Arakkoa den Haufen erst vor kurzem bereichert haben.$B$BUm die Nachfahren der Gegner Terokks beschwören zu können, werdet Ihr 10 zeitverlorene Schriftrollen benötigen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11057;
