UPDATE `locales_npc_text` SET `ID` = 11078,`Text0_0_loc3` = 'Illidans Lakaien bewachen immer noch diese Hallen, $N. Sobald sie besiegt sind, erlauben mir meine Kräfte, Euch weiter ins Innere des Tempels zu bringen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11078;
UPDATE `locales_npc_text` SET `ID` = 11081,`Text0_0_loc3` = 'Ich spüre, dass Ihr den Weg zu meinen Brüdern frei gemacht habt. Meine Verbindung zu ihnen und zum Tempel ist immer noch stark. Möchtet Ihr Euch weiter hineinwagen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11081;
UPDATE `locales_npc_text` SET `ID` = 11082,`Text0_0_loc3` = 'Meine Brüder waren vor Kurzem hier. Meine Verbindung zu ihnen ist schwach geworden. Eine machtvolle Präsenz im Hof bewacht den weiteren Weg.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 11082;
