UPDATE `locales_npc_text` SET `ID` = 11966,`Text0_0_loc3` = 'Während der Schlotternächte feiern wir den Tag, an dem die Bansheekönigin uns höchst persönlich aus dem Griff des Lichkönigs und der Geißel befreit hat. Verpasst auf keinen Fall das Fest des Weidenmannes, das jeden Abend außerhalb von Unterstadt stattfindet.',`Text0_1_loc3` = 'Während der Schlotternächte feiern wir den Tag, an dem die Bansheekönigin uns höchst persönlich aus dem Griff des Lichkönigs und der Geißel befreit hat. Verpasst auf keinen Fall das Fest des Weidenmannes, das jeden Abend außerhalb von Unterstadt stattfindet.' WHERE `locales_npc_text`.`ID` = 11966;
UPDATE `locales_npc_text` SET `ID` = 12044,`Text0_0_loc3` = 'Meine Kunden verlangen hauptsächlich nach seltenen und kostbaren Objekten.$B$BIhr könntet die Person sein, die sie mir beschafft, $C. Als Gegenleistung erhaltet Ihr, wonach es Euch begehrt.$B$BMöchtet Ihr mehr über die Sache erfahren, die es heute aufzutreiben gilt?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12044;
UPDATE `locales_npc_text` SET `ID` = 12046,`Text0_0_loc3` = 'Das Flüstern der Netherwinde spricht von einem Auftrag und Mah''duun horcht auf.$B$B$G Ein Mann : Eine Frau; tritt vor Mah''duun. Man spricht über diejenigen, deren Tod die Winde heute wünschen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12046;
