UPDATE `locales_npc_text` SET `ID` = 12773,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die natürliche Schönheit dieser Hügel verdeckt die große Dunkelheit, die darunter liegt. Seid vorsichtig.' WHERE `locales_npc_text`.`ID` = 12773;
