UPDATE `locales_npc_text` SET `ID` = 12777,`Text0_0_loc3` = 'Mein Vater überreichte mir diese Rüstung und diese Streitaxt. Bald werde ich beides für die gute Sache nutzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12777;
