UPDATE `locales_npc_text` SET `ID` = 12833,`Text0_0_loc3` = 'Ich glaube, ich habe gesehen, wie sie ihn zu der Nordseite der Abtei gebracht haben, in der Nähe der Zielscheiben.',`Text0_1_loc3` = 'Ich glaube, ich habe gesehen, wie sie ihn zu der Nordseite der Abtei gebracht haben, in der Nähe der Zielscheiben.' WHERE `locales_npc_text`.`ID` = 12833;
