UPDATE `locales_npc_text` SET `ID` = 12842,`Text0_0_loc3` = 'Was? Könnt Ihr nicht sehen, dass ich beschäftigt bin?$B$BGlaubt Ihr, diese Transmaterie bringt sich von alleine in Ordnung?!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12842;
UPDATE `locales_npc_text` SET `ID` = 12843,`Text0_0_loc3` = 'Hochlord Bolvar Fordragon kämpft für uns alle, Neuankömmling. Wäre er nicht gewesen, so würde Arthas nun auf dem Thron in Sturmwind sitzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12843;
UPDATE `locales_npc_text` SET `ID` = 12844,`Text0_0_loc3` = 'Ich bin so nah dran, das Geheimnis dieses Erzes zu entschlüsseln. Nur ein paar Minuten noch und ich habe die Lösung!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12844;
UPDATE `locales_npc_text` SET `ID` = 12845,`Text0_0_loc3` = 'Hallöchen, $C!$B$BBereit, Euch in Richtung Venturebucht aufzumach''n und ein paar Hordeschädel zu spalt''n?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12845;
UPDATE `locales_npc_text` SET `ID` = 12846,`Text0_0_loc3` = 'Ja, $G mein Sohn : meine Tochter;?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12846;
UPDATE `locales_npc_text` SET `ID` = 12848,`Text0_0_loc3` = '<Der Hochabt betracht Euch einen Moment lang und lächelt dann.>$B$BSehr gut. Ich weiß einen Ort, mein Kind. Küsst einfach meinen Ring und lasst mich wissen, wenn Ihr bereit seid.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12848;
UPDATE `locales_npc_text` SET `ID` = 12849,`Text0_0_loc3` = 'Trefft mich beim Eingang der Abtei. Ich werde in Kürze dort sein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12849;
UPDATE `locales_npc_text` SET `ID` = 12850,`Text0_0_loc3` = 'Ich habe meinen Anteil daran gehabt, ganze Zivilisationen zu zerstören... Nun so tief gesunken zu sein, dass ich mich mit dem erbärmlichen Kult der Verdammten abgeben muss, widert mich an.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12850;
UPDATE `locales_npc_text` SET `ID` = 12851,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gut, aber seid dieses Mal vorsichtiger damit!' WHERE `locales_npc_text`.`ID` = 12851;
UPDATE `locales_npc_text` SET `ID` = 12852,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid leise. Wir wollen doch nicht, dass diese Bestien auf uns aufmerksam werden!' WHERE `locales_npc_text`.`ID` = 12852;
UPDATE `locales_npc_text` SET `ID` = 12853,`Text0_0_loc3` = 'Behelligt mich nicht mit Kleinigkeiten!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12853;
UPDATE `locales_npc_text` SET `ID` = 12856,`Text0_0_loc3` = 'Könnt Ihr nicht sehen, dass ich beschäftigt bin? Die Basis wird niemals bereit für die Rückkehr des Prinzen werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12856;
UPDATE `locales_npc_text` SET `ID` = 12857,`Text0_0_loc3` = 'Hier gilt das Wort des Argentumkreuzzugs, $Gmein Freund:meine Liebe;. Wir konnten ja nicht zulassen, dass Ihr all den Ruhm einheimst!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12857;
UPDATE `locales_npc_text` SET `ID` = 12858,`Text0_0_loc3` = 'Diese verdammten Bauern müssen schneller arbeiten! Ich will heim nach Eisenschmiede. Ich vermisse meine Frau und meine Kinder ganz furchtbar.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12858;
UPDATE `locales_npc_text` SET `ID` = 12859,`Text0_0_loc3` = 'Als wir sahen, dass diese wilden Bestien die Schiffe abgebrannt hatten, sanken uns die Herzen. Niemals zuvor habe ich mich so hilflos gefühlt...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12859;
UPDATE `locales_npc_text` SET `ID` = 12860,`Text0_0_loc3` = '<Der vergessene Fußsoldat schüttelt den Kopf.>$B$BWir können so nicht weitermachen, $Gmein Freund:meine Freundin;. Wenn Prinz Arthas nicht bald zurückkehrt, sind wir alle verloren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12860;
UPDATE `locales_npc_text` SET `ID` = 12862,`Text0_0_loc3` = 'Wo kann ich Euch heute hinschicken?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12862;
UPDATE `locales_npc_text` SET `ID` = 12863,`Text0_0_loc3` = 'Es geht nichts über einen guten Verein.',`Text0_1_loc3` = 'Es geht nichts über einen guten Verein.' WHERE `locales_npc_text`.`ID` = 12863;
UPDATE `locales_npc_text` SET `ID` = 12864,`Text0_0_loc3` = 'He da, $GKamerad:Kameradin;! Wie ich sehe, habt Ihr da ein paar Braufestmarken. Zufälligerweise habe ich noch immer Braufestgegenstände zu verkaufen.',`Text0_1_loc3` = 'He da, $GKamerad:Kameradin;! Wie ich sehe, habt Ihr da ein paar Braufestmarken. Zufälligerweise habe ich noch immer Braufestgegenstände zu verkaufen.' WHERE `locales_npc_text`.`ID` = 12864;
UPDATE `locales_npc_text` SET `ID` = 12865,`Text0_0_loc3` = 'Tachchen, $R!$B$BWenn Ihr nach einem Schredder sucht, auf dem Ihr fahren könnt, so müsst Ihr Euch in Richtung der Hügel wenden.$B$BDieser hier ist außer Betrieb.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12865;
UPDATE `locales_npc_text` SET `ID` = 12867,`Text0_0_loc3` = 'Ein gutes Bier ist ein starkes Bier.',`Text0_1_loc3` = 'Ein gutes Bier ist ein starkes Bier.' WHERE `locales_npc_text`.`ID` = 12867;
UPDATE `locales_npc_text` SET `ID` = 12868,`Text0_0_loc3` = 'Nun ja, bevor die Kämpfe ausbrachen, haben wir sie hergerichtet, um die heimischen Bäume abzuernten.$B$BIn letzter Zeit sind sie jedoch gegen die Horde eingesetzt worden. Es hat sich nämlich herausgestellt, dass sie ziemlich gute Kampfmaschinen abgeben. Wenn Ihr einen Schlüssel habt, wärt Ihr ziemlich dumm, ihn nicht zu benutzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12868;
UPDATE `locales_npc_text` SET `ID` = 12869,`Text0_0_loc3` = 'Es ist toll, Mann, beim "Bier des Monats e.V." Mitglied zu sein.',`Text0_1_loc3` = 'Es ist toll, Mann, beim "Bier des Monats e.V." Mitglied zu sein.' WHERE `locales_npc_text`.`ID` = 12869;
UPDATE `locales_npc_text` SET `ID` = 12871,`Text0_0_loc3` = 'Mein Bruder, Synipus, hat jede Menge davon. Geht in Richtung Norden, zum Holzfällerposten Blauhimmel, und sprecht mit ihm.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12871;
UPDATE `locales_npc_text` SET `ID` = 12873,`Text0_0_loc3` = 'Mit genug Losen könnt Ihr dem "Bier des Monats e.V." beitreten. Werdet Mitglied des Vereins und feiert das ganze Jahr lang Braufest!$B$BJeden Monat wird Euch per Post ein neues Probebier zugeschickt. Falls es Euch schmecken sollte, könnt Ihr zur Steinfeuertaverne in Eisenschmiede gehen und noch mehr kaufen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12873;
UPDATE `locales_npc_text` SET `ID` = 12876,`Text0_0_loc3` = 'Mit genug Losen könnt Ihr dem "Bier des Monats e.V." beitreten. Werdet Mitglied des Vereins und feiert das ganze Jahr lang Braufest!$B$BJeden Monat wird Euch per Post ein neues Probebier zugeschickt. Falls es Euch schmecken sollte, dann geht nach Orgrimmar und sucht die Dunkeldornenlodge im Tal der Geister auf. Dort gibt es noch mehr zu kaufen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12876;
UPDATE `locales_npc_text` SET `ID` = 12878,`Text0_0_loc3` = 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Diese Typen stehen auf Party. Wenn es nach denen ginge, würde das Braufest nie zu Ende gehen! Letztes Jahr kam es mir ein paar Mal so vor, als wäre das der Fall.$B$BDoch jetzt sind sie auf den Dreh gekommen, dass der beste Weg zur Erhaltung des Geistes des Braufests wäre, wenn sie Leute in ihren Verein aufnehmen.$B$BGegen eine kleine Gebühr, nur ein paar Marken, könnt ihr Mitglied werden! Ihr erhaltet jeden Monat Bier per Post und die Chance noch mehr davon zu kaufen, falls Ihr es mögt. Das ist das Neuste vom Neuen und eine sichere Möglichkeit, den Geist des Braufestes niemals zu verlieren.',`Text0_1_loc3` = 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Diese Typen stehen auf Party. Wenn es nach denen ginge, würde das Braufest nie zu Ende gehen! Letztes Jahr kam es mir ein paar Mal so vor, als wäre das der Fall.$B$BDoch jetzt sind sie auf den Dreh gekommen, dass der beste Weg zur Erhaltung des Geistes des Braufests wäre, wenn sie Leute in ihren Verein aufnehmen.$B$BGegen eine kleine Gebühr, nur ein paar Marken, könnt ihr Mitglied werden! Ihr erhaltet jeden Monat Bier per Post und die Chance noch mehr davon zu kaufen, falls Ihr es mögt. Das ist das Neuste vom Neuen und eine sichere Möglichkeit, den Geist des Braufestes niemals zu verlieren.' WHERE `locales_npc_text`.`ID` = 12878;
UPDATE `locales_npc_text` SET `ID` = 12880,`Text0_0_loc3` = 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Das ist ein Haufen richtig süßer Typen, die das ganze Jahr lang das Braufest feiern wollen! In der Vergangenheit haben sie versucht, das Braufest wieder anzuzetteln, aber es hat nie lange gehalten.$B$BWenn Ihr dem Verein beitretet, bekommt Ihr jeden Monat ein anderes Bier per Post! Falls es Euch schmeckt, dann könnt Ihr mit den Händlern in Eisenschmiede reden, um noch mehr davon zu bekommen.$B$BIch denke, Ihr seid auch süß genug, um dem Verein beizutreten. Gebt mir ein paar Marken und ich gebe Euch ein Formular. Dann können wir vielleicht zusammen das ganze Jahr lang Braufest feiern!',`Text0_1_loc3` = 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Das ist ein Haufen richtig süßer Typen, die das ganze Jahr lang das Braufest feiern wollen! In der Vergangenheit haben sie versucht, das Braufest wieder anzuzetteln, aber es hat nie lange gehalten.$B$BWenn Ihr dem Verein beitretet, bekommt Ihr jeden Monat ein anderes Bier per Post! Falls es Euch schmeckt, dann könnt Ihr mit den Händlern in Eisenschmiede reden, um noch mehr davon zu bekommen.$B$BIch denke, Ihr seid auch süß genug, um dem Verein beizutreten. Gebt mir ein paar Marken und ich gebe Euch ein Formular. Dann können wir vielleicht zusammen das ganze Jahr lang Braufest feiern!' WHERE `locales_npc_text`.`ID` = 12880;
UPDATE `locales_npc_text` SET `ID` = 12881,`Text0_0_loc3` = 'Die Untoten erheben sich aus unserer Gruft!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12881;
UPDATE `locales_npc_text` SET `ID` = 12882,`Text0_0_loc3` = 'Ihr habt es geschafft, $G mein Junge:mein Mädchen;! Ich hatte ja schon gedacht, Ihr wärt erledigt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12882;
UPDATE `locales_npc_text` SET `ID` = 12883,`Text0_0_loc3` = 'Was tut Ihr denn noch immer hier? Geht zurück in die Stadt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12883;
UPDATE `locales_npc_text` SET `ID` = 12887,`Text0_0_loc3` = 'Königin Alexstrasza hat mir die Verteidigung des Wyrmruhtempels unterstellt.$B$BWie Ihr wisst, hat Malygos seine Truppen den nahegelegenen Leylinienanker zerstören lassen. Die Streitkräfte des Drachenschwarms haben dann diese Kraft zum Azurdrachenschrein umgeleitet.$B$BDie blauen Drachen benutzen diese Kraft, um Stärke zu gewinnen; so viel, dass sie es wagen, den Tempel selbst anzugreifen! Sie fangen an, Siege zu erringen.$B$BSeid Ihr hier, um uns zu helfen, $R?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12887;
UPDATE `locales_npc_text` SET `ID` = 12895,`Text0_0_loc3` = 'Hey, $C, habt Ihr das gesehen? Da draußen, in der Nähe der Wasseroberfläche... $B$BIhr habt das auch gesehen, oder?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12895;
UPDATE `locales_npc_text` SET `ID` = 12896,`Text0_0_loc3` = 'Den wa-?? Nun, ich bezeichne es als unbekanntes Schwimmobjekt. Und da wo ich herkomme, bestreiten die Meisten ihre Existenz gänzlich.$B$BAber ich weiß es besser! Ich habe sie schon von Nahem gesehen! Die letzten Jahre habe ich damit verbracht, dieses Fahrzeug zu bauen, mit dem man sie lokalisieren, verfolgen, dokumentieren und, wenn es sein muss, ausradieren kann!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12896;
UPDATE `locales_npc_text` SET `ID` = 12897,`Text0_0_loc3` = 'Lasst Euch nicht zum Narren halten, $R. Sie sind sehr schwer zu fassen. Im einen Moment sind sie hier, im nächsten weg - es ist einfach unerklärlich!$B$BUnter uns, ich habe tatsächlich schon mal ein Stück davon in meiner Hand gehabt! Aber leider, bevor ich irgendwelche Messungen durchführen konnte, war es weg! Einfach verschwunden...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12897;
UPDATE `locales_npc_text` SET `ID` = 12898,`Text0_0_loc3` = 'Was wisst Ihr schon...$B$BManche sagen, sie wären einfach nur Beobachter, manche denken, sie würden in das Leben im Meer eingreifen und wiederum andere glauben, sie wären hier, um Azeroth zu erobern!$B$BWenn ich erstmal einen mit meinen raketengetriebenen Sprengköpfen abgeschossen habe, dann kriege ich auch Antworten. Das könnt Ihr aber mal glauben!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12898;
UPDATE `locales_npc_text` SET `ID` = 12899,`Text0_0_loc3` = 'Grüße, $R.$B$BIch habe euch erwartet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12899;
UPDATE `locales_npc_text` SET `ID` = 12900,`Text0_0_loc3` = 'Wohlan denn, gehen wir!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12900;
UPDATE `locales_npc_text` SET `ID` = 12904,`Text0_0_loc3` = 'Die Greifen aus Nordends Gipfeln sind die robustesten ihrer Art.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12904;
UPDATE `locales_npc_text` SET `ID` = 12905,`Text0_0_loc3` = 'Die Greifenreiter stehen nur 10 Schritte entfernt von hier, $G mein Freund:meine Freundin;. Sie werden Euch zu Thorsons Posten bringen. Kommt in die Gänge!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12905;
UPDATE `locales_npc_text` SET `ID` = 12906,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Denkt nicht einmal für einen Augenblick, dass Ihr durch Eure früheren Leistungen irgendein Anrecht auf etwas hättet, $N. Dem ist nicht so.$B$BWenn Ihr meinen Respekt wollt, müsst Ihr ihn Euch verdienen. Dazu müsst Ihr meinen Befehlen ohne Wenn und Aber Folge leisten... ausnahmslos.' WHERE `locales_npc_text`.`ID` = 12906;
UPDATE `locales_npc_text` SET `ID` = 12909,`Text0_0_loc3` = 'Hallo. Knappe Walter und ich sind verantwortlich dafür, uns um Graumähne hier zu kümmern, und um den Rest der Reittiere auch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12909;
UPDATE `locales_npc_text` SET `ID` = 12912,`Text0_0_loc3` = 'Willkommen in Camp Oneqwah.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12912;
UPDATE `locales_npc_text` SET `ID` = 12913,`Text0_0_loc3` = 'A.L.K. ist mehr als nur ein Wurfziel für Bierkrüge, es ist auch ein wertvolles Trainingsmittel. Wenn Ihr A.L.K. treffen könnt, könnt Ihr womöglich alles treffen!$B$BPasst einfach auf, dass Ihr mir die Krüge nicht versehentlich an den Kopf werft.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12913;
UPDATE `locales_npc_text` SET `ID` = 12915,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Belgaristrasz sagt die Wahrheit; die Zeit läuft ab!$B$BIch kann Euch die Macht geben, einen Drachen des grünen Drachenschwarms herbeizurufen. Sprecht mit Belgaristrasz oder Eternos, falls Ihr es vorzieht, die Macht der Roten oder der Bronzenen zu nutzen.' WHERE `locales_npc_text`.`ID` = 12915;
UPDATE `locales_npc_text` SET `ID` = 12916,`Text0_0_loc3` = 'Varos Wolkenwanderer und seine Ringwächter schützen den zweiten Ring. Eure Drachen können sich mit den Ringwächtern in jeder Hinsicht messen, doch Varos befindet sich hinter einem undurchdringlichen Schild, der seine Energie aus der des Oculus selbst bezieht. Zehn Zentrifugenkonstrukte auf dem Ring und den Plattformen darüber liefern die Energie für den Schild. Vernichtet sie und Varos wird angreifbar sein.$B$BIch kann Euch die Kraft geben, einen Drachen des roten Schwarms zu rufen. Sprecht mit Eternos oder Verdisa, wenn Ihr es vorzieht, Euch der Macht eines bronzenen oder grünen Drachen zu bedienen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12916;
UPDATE `locales_npc_text` SET `ID` = 12917,`Text0_0_loc3` = 'Belgaristrasz sagt die Wahrheit; die Zeit läuft ab!$B$BIch kann Euch die Macht geben, einen Drachen des Bronzeschwarms herbeizurufen. Sprecht mit Belgaristrasz oder Verdisa, falls Ihr es vorzieht, die Macht der Roten oder der Grünen zu nutzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12917;
UPDATE `locales_npc_text` SET `ID` = 12926,`Text0_0_loc3` = 'Die meisten meiner Brüder verachten die "barbarische" Art der Horde. Dazu zählte ich auch einst. Als ich zu Anfang an diesen etwas feuchtwarmen und... geruchvollen Ort beordert wurde, verfluchte ich mein Schicksal.$B$BWährend meiner Studien lernte ich jedoch meine Umgebung zu schätzen. Sie hat einen gewissen Reiz, findet Ihr nicht?$B$BAch, und bei meinen Studien ging es darum, anderen Magiern mit einem Zauber zu erlauben, hierher zurückzukehren. Soll ich es Euch lehren?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12926;
UPDATE `locales_npc_text` SET `ID` = 12927,`Text0_0_loc3` = 'Die meisten meiner Brüder verachten durch die "barbarische" Art der Horde. Dazu zählte ich auch einst. Als ich zu Anfang an diesen etwas feuchtwarmen und... geruchvollen Ort beordert wurde, verfluchte ich mein Schicksal.$B$BWährend meiner Studien lernte ich jedoch meine Umgebung zu schätzen. Sie hat einen gewissen Reiz, findet Ihr nicht?$B$B... Da Ihr kein Magier seid, werdet Ihr an meinen Studien sicher kein Interesse haben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12927;
UPDATE `locales_npc_text` SET `ID` = 12929,`Text0_0_loc3` = 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen. Nun, wie kann ich Euch behilflich sein?',`Text0_1_loc3` = 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen. Nun, wie kann ich Euch behilflich sein?' WHERE `locales_npc_text`.`ID` = 12929;
UPDATE `locales_npc_text` SET `ID` = 12930,`Text0_0_loc3` = 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen...',`Text0_1_loc3` = 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen...' WHERE `locales_npc_text`.`ID` = 12930;
UPDATE `locales_npc_text` SET `ID` = 12932,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Lehren nützen nur den Magiern. Ich kann Euch nicht helfen, $C.' WHERE `locales_npc_text`.`ID` = 12932;
UPDATE `locales_npc_text` SET `ID` = 12933,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Sinu a''manore. Falls Ihr danach strebt, ein Portal nach Theramore zu öffnen, kann ich es Euch zeigen.' WHERE `locales_npc_text`.`ID` = 12933;
UPDATE `locales_npc_text` SET `ID` = 12934,`Text0_0_loc3` = 'Seitdem das Braufest zum ersten Mal stattgefunden hat, haben sich ein paar Dinge verändert. Wir geben nun keine Lose mehr aus, sondern Braufestbiermarken. Falls Ihr alte Lose haben solltet, dann kann ich sie für Euch umtauschen.',`Text0_1_loc3` = 'Seitdem das Braufest zum ersten Mal stattgefunden hat, haben sich ein paar Dinge verändert. Wir geben nun keine Lose mehr aus, sondern Braufestbiermarken. Falls Ihr alte Lose haben solltet, dann kann ich sie für Euch umtauschen.' WHERE `locales_npc_text`.`ID` = 12934;
UPDATE `locales_npc_text` SET `ID` = 12937,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wollt Ihr reisen, $R?$B$BOder habt Ihr etwas anderes im Sinn?' WHERE `locales_npc_text`.`ID` = 12937;
UPDATE `locales_npc_text` SET `ID` = 12938,`Text0_0_loc3` = 'Nun hört mal zu. Wenn Ihr nach Antworten sucht, dann geht nichts über eine gute Explosion - je größer, je besser!$B$BAber damit ist es wahrscheinlich fürs Erste genug der Fragen, $R.$B$BMeine Sprengköpfe sind Geheimsache und das Element 115, das ich entwickelt habe, um sie anzutreiben, ist sogar noch geheimer!$B$BGenau genommen wäre es sogar am besten, wenn Ihr in Euer normales Leben zurückkehren und so tun würdet, als hätten wir einander nie getroffen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12938;
UPDATE `locales_npc_text` SET `ID` = 12939,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid willkommen. Ihr trefft zur rechten Zeit ein.' WHERE `locales_npc_text`.`ID` = 12939;
UPDATE `locales_npc_text` SET `ID` = 12940,`Text0_0_loc3` = 'Thel''zan muss sterben, und sei es nur, damit das permanente Gejammer endlich aufhört!',`Text0_1_loc3` = 'Thel''zan muss sterben, und sei es nur, damit das permanente Gejammer endlich aufhört!' WHERE `locales_npc_text`.`ID` = 12940;
UPDATE `locales_npc_text` SET `ID` = 12941,`Text0_0_loc3` = 'Offizieller Aushang der Bürgerschaft und der Verwaltung Wintergardes.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12941;
UPDATE `locales_npc_text` SET `ID` = 12942,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Macht Euch die Anwesenheit eines Schwarzen Drachen nervös?$B$BGut. Das sollte sie auch. Diesen Drachen geht es ebenso, aber sie klammern sich an ihren uralten Tugenden fest und würden es nicht wagen, mich hier anzugreifen.$B$BDumm von ihnen.' WHERE `locales_npc_text`.`ID` = 12942;
UPDATE `locales_npc_text` SET `ID` = 12943,`Text0_0_loc3` = 'Hört zu, $R.$B$BWenn Ihr einen Schredder sucht, um darauf zu reiten, so müsst Ihr Euch in die Hügel nördlich von hier begeben.$B$BDieser hier ist außer Betrieb.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12943;
UPDATE `locales_npc_text` SET `ID` = 12944,`Text0_0_loc3` = 'Zunächst haben wir sie repariert, um etwas zu haben, mit dem man sich die Wälder hier zunutze machen kann. Als dann aber die Allianz hier auftauchte und die Kämpfe ausbrachen, haben wir sie als Kriegsmaschinen eingesetzt.$B$BGlaubt mir, $C. Wenn Ihr einen Schlüssel hättet, wärt Ihr sehr dumm, so ein Ding nicht zu nutzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12944;
UPDATE `locales_npc_text` SET `ID` = 12945,`Text0_0_loc3` = 'Grekk hat sie.$B$BVielleicht gibt er Euch einen, wenn Ihr ihm aushelft. Das nächste Mal, wenn Ihr im Holzfällerposten Blauhimmel seid, dann sprecht mit ihm.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12945;
UPDATE `locales_npc_text` SET `ID` = 12946,`Text0_0_loc3` = 'Ja?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12946;
UPDATE `locales_npc_text` SET `ID` = 12949,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der ewige Drachenschwarm versucht, das Schicksal von Prinz Arthas Menethil zu verändern. Ihr kennt ihn als den Lichkönig, aber hier und jetzt war er noch ein Prinz von Lordaeron, der das Beste für sein Königreich wollte. Arthas trifft heute eine verhängnisvolle Entscheidung, die etwas bewirkt, das wir Hüter der Zeit einen Wendepunkt in der Zeitlinie nennen.' WHERE `locales_npc_text`.`ID` = 12949;
UPDATE `locales_npc_text` SET `ID` = 12950,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Arthas gab den Befehl, Stratholme zu säubern und jeden Menschen innerhalb der Stadtmauern zu töten. Er fand Beweise dafür, dass die Geißel die Stadt mit ihrer heimtückischen Seuche infiziert hatte, und dass einen diese nicht einfach umbringt. Wer an dieser Seuche stirbt, steht als hirnloser Zombie von den Toten auf und dient als weiteres Werkzeug der Kriegsmaschinerie der Geißel.' WHERE `locales_npc_text`.`ID` = 12950;
UPDATE `locales_npc_text` SET `ID` = 12952,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Ewigen planen etwas ungewöhnlich Hinterhältiges. Sie versuchen, den Beweis zu verstecken, der dazu führen wird, dass Arthas Stratholme auslöscht. Ihre Agenten haben die Lieferungen aus verseuchtem Korn mit Illusionsmagie belegt, sodass sie normal erscheinen. Ihr müsst das versteckte Korn finden und diesen arkanen Disruptor bei den Lieferungen anwenden. Arthas'' Männer suchen nach verseuchtem Korn und werden es rasch finden, wenn die Illusion beseitigt wurde.' WHERE `locales_npc_text`.`ID` = 12952;
UPDATE `locales_npc_text` SET `ID` = 12953,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich glaube nicht, dass wir es uns leisten können, diese Möglichkeit, uns beim roten Drachenschwarm beliebt zu machen, ungenutzt zu lassen. Bei den bevorstehenden Schlachten ist ihre Unterstützung unbezahlbar.' WHERE `locales_npc_text`.`ID` = 12953;
UPDATE `locales_npc_text` SET `ID` = 12954,`Text0_0_loc3` = 'In längst vergangenen Jahren bediente sich der Kriegsherr Nekros Schädelbrecher eines mächtigen Artefakts, um Alexstrasza und einen Großteil des roten Drachenschwarms zu versklaven. Zuluhed und Nekros sind tot, aber die Schuld, die wir gegenüber den Roten auf uns geladen haben, ist noch lange nicht zurückgezahlt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12954;
UPDATE `locales_npc_text` SET `ID` = 12955,`Text0_0_loc3` = 'Die Allianz hat diese abgelegene Hütte einige Zeit unterhalten. Sie wurde ab und zu von unseren Jägern und Förstern genutzt.$B$BNun, da Krieg herrscht, könnt Ihr aber sicher sein, dass das Militär hier das Sagen hat.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12955;
UPDATE `locales_npc_text` SET `ID` = 12958,`Text0_0_loc3` = '<Der Kommandant unterbricht seinen Unterricht und dreht sich um, während sein abschätzender und bewertender Blick auf Euch ruht.>$B$BJemanden wie Euch kann ich gebrauchen, $C.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12958;
UPDATE `locales_npc_text` SET `ID` = 12960,`Text0_0_loc3` = 'Ja, $C, was gibt''s?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12960;
UPDATE `locales_npc_text` SET `ID` = 12961,`Text0_0_loc3` = 'Es ist gut, Euch wiederzusehen, $N.$B$BKommt Ihr, um Eure Kenntnisse in Geißeltaktik aufzufrischen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12961;
UPDATE `locales_npc_text` SET `ID` = 12963,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Liebling, für diese Schuhe könnte man sterben! Das meine ich wörtlich. Wen hast du dafür umgebracht?' WHERE `locales_npc_text`.`ID` = 12963;
UPDATE `locales_npc_text` SET `ID` = 12965,`Text0_0_loc3` = 'Warte auf Befehl!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12965;
UPDATE `locales_npc_text` SET `ID` = 12966,`Text0_0_loc3` = 'Willkommen in Granitquell, $Gmein Freund:meine Freundin;.$B$BSollte es Euch gelingen, einen der Eistrolle lebendig einzufangen, so gebt mir auf jeden Fall Bescheid. Wir zahlen gut für sie.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12966;
UPDATE `locales_npc_text` SET `ID` = 12968,`Text0_0_loc3` = 'He, wie ich sehe, habt Ihr ein paar Braufestbiermarken. Wenn Ihr wollt, dann kann ich Euch noch ein paar Braufestsachen verkaufen.',`Text0_1_loc3` = 'He, wie ich sehe, habt Ihr ein paar Braufestbiermarken. Wenn Ihr wollt, dann kann ich Euch noch ein paar Braufestsachen verkaufen.' WHERE `locales_npc_text`.`ID` = 12968;
UPDATE `locales_npc_text` SET `ID` = 12976,`Text0_0_loc3` = NULL,`Text0_1_loc3` = '$N, schön Euch zu sehen!$B$BOder ist dies das erste Mal, dass wir uns begegnen?$B$BIch war bereits an so vielen Orten und in so vielen Zeiten, dass ich manchmal ein wenig den Überblick verliere.' WHERE `locales_npc_text`.`ID` = 12976;
UPDATE `locales_npc_text` SET `ID` = 12977,`Text0_0_loc3` = 'Seuche, Seuche, Seuche, man hört nichts anderes hier. All die vielen Möglichkeiten, die es gibt und ich?!$B$BAh, hierher, hierher. Ihr habt Haustiere, ja? Natürlich habt Ihr. Natürlich. Kleine Haustiere. Ich habe eine Mixtur für sie. Sie macht sie zu einem Kad... äh, kräftigen und klugen Kreaturen, ja. Ja. Kräftig.$B$BVersucht es!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12977;
UPDATE `locales_npc_text` SET `ID` = 12978,`Text0_0_loc3` = 'Vorsichtig damit. Sie ist instabil und verliert schnell ihre Wirksamkeit. Benutzt sie schnell! Und... bevorzugterweise in meinem Blickfeld, ja...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12978;
UPDATE `locales_npc_text` SET `ID` = 12982,`Text0_0_loc3` = 'Ich verließ einst einen Krieg, um mich um die Probleme zu Hause in Westfall zu kümmern. Es scheint Ewigkeiten her zu sein. Dem Licht sei Dank, dass die Defias nicht länger eine Bedrohung sind.$B$BWir sind hier weit weg von der Heimat, aber wenn wir diese Schlacht nicht gewinnen, wird es vielleicht kein Westfall mehr geben, in das wir zurückkehren können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12982;
UPDATE `locales_npc_text` SET `ID` = 12990,`Text0_0_loc3` = 'Meine Kriegsmagier sind auf dem Weg und werden in Kürze eintreffen.$B$BSprecht mich erneut an, sobald sie zurückgekehrt sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12990;
UPDATE `locales_npc_text` SET `ID` = 12991,`Text0_0_loc3` = 'Verzeiht mir, $R, aber ich bin im Dienst und darf nicht gestört werden.$B$BBitte entschuldigt mich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12991;
UPDATE `locales_npc_text` SET `ID` = 12992,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gute Arbeit! Arthas weiß jetzt vom verseuchten Korn in Stratholme und er wird mit dem Ausmerzen beginnen. Aber irgendetwas stimmt noch nicht. Ich spüre eine fremde Präsenz neben uns in dieser Zeitlinie und das muss bedeuten, dass der ewige Drachenschwarm hier irgendwo ist.' WHERE `locales_npc_text`.`ID` = 12992;
UPDATE `locales_npc_text` SET `ID` = 12993,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich weiß es nicht und das ist beunruhigend. Ich tue, was ich kann, um die Unendlichen zu finden. Ihr müsst jetzt in Arthas'' Nähe bleiben, indem Ihr Euch seiner Armee anschließt.' WHERE `locales_npc_text`.`ID` = 12993;
UPDATE `locales_npc_text` SET `ID` = 12994,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr müsst keine Anwerbungsformulare oder etwas Derartiges unterschreiben, aber Ihr müsst an seiner Seite kämpfen. Ihr müsst dafür sorgen, dass Arthas Stratholme auslöscht und Mal''ganis besiegt. Ohne Uther und Jaina in seiner Nähe braucht er jede Hilfe, die er kriegen kann. Wenn Ihr mit Arthas redet, wird er Euch auftragen, Mal''ganis’ Streitkräfte zu vernichten. Spielt so lange mit, bis ich herausgefunden habe, was es mit den Ewigen auf sich hat. Ich melde mich bei Euch, wenn ich mehr weiß.' WHERE `locales_npc_text`.`ID` = 12994;
UPDATE `locales_npc_text` SET `ID` = 12995,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Viel Glück und passt auf Euch auf. Da kommen Uther und Jaina.' WHERE `locales_npc_text`.`ID` = 12995;
UPDATE `locales_npc_text` SET `ID` = 12998,`Text0_0_loc3` = '<Drakuru streckt seine blutende Hand durch die Gitterstäbe und schaut Euch erwartungsvoll an.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12998;
UPDATE `locales_npc_text` SET `ID` = 12999,`Text0_0_loc3` = 'Es gab Zeiten, da hätt'' ich Euer Herz rausgeriss''n, nur um Euren Gesichtsausdruck zu seh''n...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 12999;
UPDATE `locales_npc_text` SET `ID` = 13001,`Text0_0_loc3` = 'Ey, Mann!$B$BSchön, dich wiederzuseh''n... $N, richtig? Ja, ja, natürlich.$B$BDu und ich, wir werd''n gute Freunde werden. Drakuru weiß solche Sach''n.$B$B<Langsam zieht sich ein Grinsen über das bemalte Gesicht des Trolls.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13001;
UPDATE `locales_npc_text` SET `ID` = 13002,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13002;
UPDATE `locales_npc_text` SET `ID` = 13003,`Text0_0_loc3` = 'Von diesem Throne aus erteile ich die Befehle Höllschreis. Sein Wort ist Leben und sein Wort ist Tod.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13003;
UPDATE `locales_npc_text` SET `ID` = 13004,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Elune wacht über Euch, $R.' WHERE `locales_npc_text`.`ID` = 13004;
UPDATE `locales_npc_text` SET `ID` = 13005,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13005;
UPDATE `locales_npc_text` SET `ID` = 13006,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13006;
UPDATE `locales_npc_text` SET `ID` = 13009,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dies ist die einzige Möglichkeit, dass Harry überhaupt mit mir spricht! Ich muss diese blöde Maske die ganze Zeit aufbehalten...' WHERE `locales_npc_text`.`ID` = 13009;
UPDATE `locales_npc_text` SET `ID` = 13011,`Text0_0_loc3` = 'Ey, Mann!$B$BEs ist nicht das, wonach es aussehen tut. Na ja, vielleicht doch...$B$BEs is'' kompliziert.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13011;
UPDATE `locales_npc_text` SET `ID` = 13012,`Text0_0_loc3` = 'Hallo, Mann! Kenn ich dich?$B$BAhh, ja, Prigmon vergisst nie''n Gesicht. $B$BIhr... Verfolgste mich, Mann? Weil, was auch immer es war, Prigmon hat''s nich'' gemacht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13012;
UPDATE `locales_npc_text` SET `ID` = 13018,`Text0_0_loc3` = 'Seid gegrüßt, $R.$B$BWenn Ihr daran interessiert sein solltet, bei der Verteidigung des Tempels zu helfen, dann sprecht mit Lord Afrasastrasz im Inneren.',`Text0_1_loc3` = 'Seid gegrüßt, $R.$B$BWenn Ihr daran interessiert sein solltet, bei der Verteidigung des Tempels zu helfen, dann sprecht mit Lord Afrasastrasz im Inneren.' WHERE `locales_npc_text`.`ID` = 13018;
UPDATE `locales_npc_text` SET `ID` = 13019,`Text0_0_loc3` = 'Willkommen in meinem Lager, $R.$B$BEs mag nich'' nach viel aussehen, aber es ist mein Zuhause.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13019;
UPDATE `locales_npc_text` SET `ID` = 13022,`Text0_0_loc3` = 'Wie kann ich Euch helfen, mein Kind?',`Text0_1_loc3` = 'Wie kann ich Euch helfen, mein Kind?' WHERE `locales_npc_text`.`ID` = 13022;
UPDATE `locales_npc_text` SET `ID` = 13023,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Mr. Floppy und ich sind spazieren gegangen, aber dann haben wir uns verlaufen und ich habe ganz doll Angst gekriegt, weil da waren diese Wölfe, wisst Ihr, und sie haben uns gejagt, aber dann habe ich diesen Turm gesehen und Mr. Floppy und ich haben uns hier versteckt und nach einiger Zeit sind die Wölfe dann weggelaufen und es war ganz schrecklich und jetzt wissen wir nicht, wie wir nach Hause kommen sollen.' WHERE `locales_npc_text`.`ID` = 13023;
UPDATE `locales_npc_text` SET `ID` = 13024,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13024;
UPDATE `locales_npc_text` SET `ID` = 13025,`Text0_0_loc3` = 'Ihr seid willkommen im Wyrmruhtempel, $R. Verzeiht die Aufruhr draußen; Malygos'' kürzliche Handlungen haben uns in einen Belagerungszustand versetzt. Wenn Ihr dennoch hier rasten wollt, so steht Euch unser Schutz natürlich zur Verfügung.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13025;
UPDATE `locales_npc_text` SET `ID` = 13026,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13026;
