UPDATE `locales_npc_text` SET `ID` = 13321,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Von diesem Dock aus fährt die Bravado zwischen Sturmwind und Auberdine hin und her.' WHERE `locales_npc_text`.`ID` = 13321;
UPDATE `locales_npc_text` SET `ID` = 13322,`Text0_0_loc3` = 'Meine arme Flugmaschine. Sie hat den Absturz nicht überlebt und ohne Ersatzteile kriege ich sie nie wieder zum Laufen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13322;
UPDATE `locales_npc_text` SET `ID` = 13324,`Text0_0_loc3` = 'Schon bald, $N!$B$BSchon bald werdet Ihr den Geschmack wahrer Macht kennen lernen. Schon bald werdet Ihr für Euren Einsatz und Eure Dienste belohnt werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13324;
UPDATE `locales_npc_text` SET `ID` = 13325,`Text0_0_loc3` = 'Ihr seid auf dem Pfad, Sterblicher...$B$BHaltet aus und Ihr erhaltet große Macht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13325;
UPDATE `locales_npc_text` SET `ID` = 13326,`Text0_0_loc3` = 'Mphmm rmphhimm rrhumghph?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13326;
UPDATE `locales_npc_text` SET `ID` = 13328,`Text0_0_loc3` = 'Willkommen, darf ich Euch etwas anbieten?',`Text0_1_loc3` = 'Willkommen, darf ich Euch etwas anbieten?' WHERE `locales_npc_text`.`ID` = 13328;
UPDATE `locales_npc_text` SET `ID` = 13331,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gefällt Euch das neue Heim, das ich aus meinen sogenannten Anhängern gefertigt habe?$B$BWährend meines Todeskampfs habe ich sie alle verflucht und mit mir in diese besondere Unterwelt meiner eigenen Schöpfung gezogen.$B$BIch denke, ich werde mich an ihren ewigen Qualen erfreuen, doch was ich wirklich will, ist Rache an meinem früheren Propheten zu üben!' WHERE `locales_npc_text`.`ID` = 13331;
UPDATE `locales_npc_text` SET `ID` = 13342,`Text0_0_loc3` = 'Seid Ihr gekommen, um von Hargus etwas zu kaufen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13342;
UPDATE `locales_npc_text` SET `ID` = 13348,`Text0_0_loc3` = 'Noch''n Ersucher der Gunst vom Lichkönig?$B$BNur Würdige erhalten die dunklen Gaben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13348;
UPDATE `locales_npc_text` SET `ID` = 13349,`Text0_0_loc3` = 'Willkommen bei der Gildenregistrierung von Dalaran. Wie kann ich Euch helfen?',`Text0_1_loc3` = 'Willkommen bei der Gildenregistrierung von Dalaran. Wie kann ich Euch helfen?' WHERE `locales_npc_text`.`ID` = 13349;
UPDATE `locales_npc_text` SET `ID` = 13352,`Text0_0_loc3` = 'Ich hoffe, der Erzmagier weiß zu schätzen, was ich durchmache, um Hemets Teil der Vereinbarung zu erfüllen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13352;
UPDATE `locales_npc_text` SET `ID` = 13353,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr solltet im Moment in Arthas'' Nähe bleiben!' WHERE `locales_npc_text`.`ID` = 13353;
UPDATE `locales_npc_text` SET `ID` = 13355,`Text0_0_loc3` = 'Wir werden ihnen alles nehmen, Todesritter.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13355;
UPDATE `locales_npc_text` SET `ID` = 13359,`Text0_0_loc3` = 'Wildherzen nehmen Land. Großzungen gehen oder wir töten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13359;
UPDATE `locales_npc_text` SET `ID` = 13360,`Text0_0_loc3` = 'Wildherzen können böse werden, aber wir gute Leute. Du sehen, wenn du bleiben bisschen. Wir gute Freunde mit Klug und Stark.$B$BWir nur nix mögen Dumm.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13360;
UPDATE `locales_npc_text` SET `ID` = 13361,`Text0_0_loc3` = 'Hier Großes Regenstein. Wir beschützen und ihm geben Glitzer-Glitzer und er sich um uns kümmern.$B$BDu mehr Glitzer-Glitzer für Stein vielleicht?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13361;
UPDATE `locales_npc_text` SET `ID` = 13362,`Text0_0_loc3` = 'Wo ich nix Größe, ich stark Magie. Ich mich kümmern um Großes Nebelstein gut... meistens jedenfalls.$B$BBlöde Wauwauleute.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13362;
UPDATE `locales_npc_text` SET `ID` = 13363,`Text0_0_loc3` = 'Wauwauleute uns angreifen seit Tagen. Großes Blitzstein noch immer sicher aber.$B$BSoo-holu kommen uns helfen, wie schon oft... Wauwauleute keine Chance.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13363;
UPDATE `locales_npc_text` SET `ID` = 13364,`Text0_0_loc3` = 'Kartak kommt für Großzungenfleisch. Wildherzen sicherstellen, dass viel bereit.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13364;
UPDATE `locales_npc_text` SET `ID` = 13365,`Text0_0_loc3` = 'Hallo Trockenhaut.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13365;
UPDATE `locales_npc_text` SET `ID` = 13366,`Text0_0_loc3` = 'Alles okay?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13366;
UPDATE `locales_npc_text` SET `ID` = 13367,`Text0_0_loc3` = 'Hallo! Wie geht''s? Uns gut... jup, gut, gut. Hallo!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13367;
UPDATE `locales_npc_text` SET `ID` = 13368,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich stehe für immer in Eurer Schuld, $N.' WHERE `locales_npc_text`.`ID` = 13368;
UPDATE `locales_npc_text` SET `ID` = 13375,`Text0_0_loc3` = 'Ein schöner Tag zum Fliegen, oder?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13375;
UPDATE `locales_npc_text` SET `ID` = 13376,`Text0_0_loc3` = 'Ich erkenne ein Ass, wenn ich eins sehe. Danke nochmals, dass Ihr mein Leben gerettet habt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13376;
UPDATE `locales_npc_text` SET `ID` = 13382,`Text0_0_loc3` = 'Schaut, was Ihr angerichtet habt, Todesritter! Ihr habt der Geißel Ruhm gebracht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13382;
UPDATE `locales_npc_text` SET `ID` = 13390,`Text0_0_loc3` = 'Lass mich hier raus, Mann!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13390;
UPDATE `locales_npc_text` SET `ID` = 13391,`Text0_0_loc3` = 'Die Geißel is'' in Zul''Drak nicht willkommen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13391;
UPDATE `locales_npc_text` SET `ID` = 13395,`Text0_0_loc3` = 'Im Moment mag es noch wie ein gewöhnliches Kürbisbeet aussehen, aber sobald der Seuchenkessel platziert ist, wird daraus eine Quelle von Krankheit, Fäule und Seuche!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13395;
UPDATE `locales_npc_text` SET `ID` = 13396,`Text0_0_loc3` = 'Wie könnt Ihr es wagen? Geht mir aus den Augen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13396;
UPDATE `locales_npc_text` SET `ID` = 13397,`Text0_0_loc3` = 'Es ist nur eine Frage der Zeit, bis die Seuche Neu-Avalon übernimmt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13397;
UPDATE `locales_npc_text` SET `ID` = 13402,`Text0_0_loc3` = 'Fineous hat, was Ihr braucht, $G mein Freund:meine Freundin;.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13402;
UPDATE `locales_npc_text` SET `ID` = 13419,`Text0_0_loc3` = 'Kreuzfahrer...$B$B<Totenschwur schüttelt den Kopf.>$B$BSo etwas wie sie habe ich unter den Menschen noch nie gesehen. Meine Theorie ist, dass sie über keine höhere Gehirnfunktion verfügen und dadurch unfähig sind, eine Situation logisch zu durchdenken und eine Lösung für sie zu finden. Man muss nur das Licht erwähnen und schon sind sie ganz wild auf eine Schlacht... An diesem Punkt stürzen sie sich dann in jeden Kampf, ohne Rücksicht auf das eigene Wohlergehen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13419;
UPDATE `locales_npc_text` SET `ID` = 13422,`Text0_0_loc3` = '$N! Ich habe auf deine Rückkehr gewartet. Was hat dich so lang'' aufgehalten, Mann?$B$BEs is'' Zeit, die Konvertierung des Seuchenbluts zu beenden.$B$BSchließ dich mir an und werde Zeuge der Erschaffung unserer Armee!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13422;
UPDATE `locales_npc_text` SET `ID` = 13425,`Text0_0_loc3` = 'Ich bin schwer verletzt, doch ich tue, was ich kann, um zu helfen. Versucht, innerhalb meiner antimagischen Barriere zu bleiben oder die Magie der Akolyten wird Euch in Stücke reißen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13425;
UPDATE `locales_npc_text` SET `ID` = 13426,`Text0_0_loc3` = 'Was kann ich für Euch tun, $R?',`Text0_1_loc3` = 'Was kann ich für Euch tun, $R?' WHERE `locales_npc_text`.`ID` = 13426;
