UPDATE `locales_npc_text` SET `ID` = 13431,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist nicht ganz das, wofür ich mich gemeldet hatte, aber, hey,… wenigstens ist es hier warm.' WHERE `locales_npc_text`.`ID` = 13431;
