UPDATE `locales_npc_text` SET `ID` = 13454,`Text0_0_loc3` = 'Seid gegrüßt, $C! Der Hafen von Sturmwind bietet einen wunderschönen Anblick. Und aus der Luft kann man ihn sogar noch mehr genießen. Wie würde es Euch gefallen, den Hafen aus der Vogelperspektive zu sehen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13454;
UPDATE `locales_npc_text` SET `ID` = 13455,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich glaube nicht, dass Euer Verstand die Schärfe besitzt, die Natur der Portalmagie zu verstehen.$B$BAllerdings könnt Ihr den Kristall hinter mir benutzen, um Euch zu den Ruinen unterhalb Dalarans in Kristallsang zu teleportieren.' WHERE `locales_npc_text`.`ID` = 13455;
UPDATE `locales_npc_text` SET `ID` = 13456,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Als Magier seid Ihr Mitglied einer erlesenen Gruppe, die ein Portal herbeizaubern kann, das Euch wieder an genau diesen Platz zurückbringen kann. Ich kann es Euch beibringen, wenn Ihr bereit seid.$B$BUnd denkt daran, dass Ihr stets den Kristall hinter mir benutzen könnt, um Euch zu den Ruinen unterhalb Dalarans im Kristallsangwald zu teleportieren.' WHERE `locales_npc_text`.`ID` = 13456;
