UPDATE `locales_npc_text` SET `ID` = 13800,`Text0_0_loc3` = 'Ich bin meinem Schicksal lange genug davongelaufen. Mir war nie klarer, was ich zu tun habe.$B$BLoken muss durch meine eigene Hand vernichtet werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13800;
UPDATE `locales_npc_text` SET `ID` = 13801,`Text0_0_loc3` = 'Einen Augenblick lang dachte ich, Ihr wärt meine geliebte Sif... diese Haarsträhne... Aber nein! Es kann nicht sein... Sif ist tot...$B$BMein... mein Bruder... hat sie getötet!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13801;
UPDATE `locales_npc_text` SET `ID` = 13802,`Text0_0_loc3` = 'Ulduar... Mir war es auferlegt, Ulduar zu schützen.$B$BDie Terrasse der Schöpfer zu schützen.$B$BDie Bündnisse zwischen den Völkern des Frosts zu schützen.$B$BDoch ich habe versagt! Ich ließ mich von meinem Bruder täuschen. Seinetwegen wandte ich mich gegen meine Verbündeten. Ihm gelang es, die Schuld an Sifs Tod einem guten Freund, Arngrim, dem König der Frostriesen, zuzuschieben.$B$BNachdem mir klar geworden war, was ich getan hatte... überkam mich... Verzweiflung.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13802;
UPDATE `locales_npc_text` SET `ID` = 13803,`Text0_0_loc3` = 'Wer seid Ihr, kleines Wesen? Wer seid Ihr, dass Ihr es wagt, solche Worte gegenüber dem mächtigen Thorim zu benutzen...$B$BIch sollte... Euch... zermalmen!$B$BUnd doch habt Ihr die Benommenheit von mir genommen. Eure Anwesenheit hier ist ein Zeichen!$B$BEs war kein Zufall, dass ich Euch mit Sif verwechselt habe. Was würde sie nur von mir denken? Ich muss mich zum Kampf bereitmachen.$B$BIch muss ihren Tod sühnen und den heiligen Boden der Titanen dem wahnsinnigen Griff meines Bruders entreißen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 13803;
