UPDATE `locales_npc_text` SET `ID` = 14052,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ach du meine Güte! Wollt Ihr wohl leise sein, $N?! Was? Versucht Ihr, uns alle umzubringen?!$B$BNatürlich weiß ich, wer Ihr seid! Wer in der Allianz kennt nicht den Namen $N?!$B$BSagt, was haltet Ihr von meinem Hüpfboter? Er basiert auf Mechagnomdesign. Schnittig und aerodynamisch!' WHERE `locales_npc_text`.`ID` = 14052;
