UPDATE `locales_npc_text` SET `ID` = 14055,`Text0_0_loc3` = 'Der Weg, der vor uns liegt, wird kein leichter sein. Wenn Ihr über einen starken Arm verfügt, so bleibt in der Nähe, bis einer meiner Trupps bereit ist, nach Ymirheim aufzubrechen. Sie können all die Hilfe brauchen, die sie kriegen können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14055;
UPDATE `locales_npc_text` SET `ID` = 14056,`Text0_0_loc3` = 'Unser Schiff "Die Zuversicht" ist irgendwie mit Käfern überschwemmt worden und es ist uns unmöglich, die Segel nach Nordend zu setzen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14056;
UPDATE `locales_npc_text` SET `ID` = 14061,`Text0_0_loc3` = 'Na, Ihr seid ja ein hässlicher Vogel! Aber das muss ja nicht heißen, dass Ihr nicht wissen würdet, wie man kämpft.$B$BEiner meiner Trupps sollte eigentlich demnächst abmarschbereit sein - bewegt Euren Hintern in die Reihe und macht mit ihnen zusammen den Aufstieg!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14061;
