UPDATE `locales_npc_text` SET `ID` = 14065,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieses Portal führt zu den Höhlen der Zeit, tief unter der Erde im fernen Tanaris.$B$BIch verfüge über die Macht, Euch dorthin zu schicken, wenn Ihr dies wünscht.' WHERE `locales_npc_text`.`ID` = 14065;
